import React, { useState, useEffect } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AddLecture = () => {
  const [title, setTitle] = useState("");
  const [courseId, setCourseId] = useState("");
  const [instructorId, setInstructorId] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Fetch all courses and instructors
  useEffect(() => {
    const fetchCoursesAndInstructors = async () => {
      try {
        const [coursesRes, instructorsRes] = await Promise.all([
          axios.get("http://localhost:5000/api/admin/courses"),
          axios.get("http://localhost:5000/api/admin/instructors"),
        ]);

        setCourses(coursesRes.data);
        setInstructors(instructorsRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      }
    };

    fetchCoursesAndInstructors();
  }, []);

  const handleAddLecture = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const res = await axios.post("http://localhost:5000/api/admin/lectures", {
        title,
        courseId,
        instructorId,
        date,
        time,
      });

      setMessage("Lecture added successfully!");
      setTitle("");
      setCourseId("");
      setInstructorId("");
      setDate("");
      setTime("");
    } catch (err) {
      console.error("Error adding lecture:", err);
      setMessage(err.response?.data?.message || "Something went wrong");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="container">
        <h2>Add New Lecture</h2>
        {loading && <Loader />}
        {message && <p>{message}</p>}

        <form onSubmit={handleAddLecture}>
          <div>
            <label>Lecture Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div>
            <label>Course:</label>
            <select
              value={courseId}
              onChange={(e) => setCourseId(e.target.value)}
              required
            >
              <option value="">Select Course</option>
              {courses.map((course) => (
                <option key={course._id} value={course._id}>
                  {course.title}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Instructor:</label>
            <select
              value={instructorId}
              onChange={(e) => setInstructorId(e.target.value)}
              required
            >
              <option value="">Select Instructor</option>
              {instructors.map((inst) => (
                <option key={inst._id} value={inst._id}>
                  {inst.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Date:</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>

          <div>
            <label>Time:</label>
            <input
              type="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              required
            />
          </div>

          <button type="submit" disabled={loading}>
            Add Lecture
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddLecture;
