import React, { useState } from "react";
import api from "../../api/axios";
import { useNavigate } from "react-router-dom";

const InstructorLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await api.post("/instructor/login", { email, password });

      localStorage.setItem("instructorToken", res.data.token);

      navigate("/instructor/dashboard");
    } catch (err) {
      alert(err.response?.data?.message || "Login failed");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form className="p-6 bg-white shadow rounded" onSubmit={handleLogin}>
        <h2 className="text-xl mb-4 font-semibold">Instructor Login</h2>

        <input
          type="email"
          className="border p-2 w-full my-2"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <input
          type="password"
          className="border p-2 w-full my-2"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button className="bg-green-600 text-white py-2 px-4 rounded w-full">
          Login
        </button>

        <p className="mt-3 text-sm">
          No account?{" "}
          <span
            className="text-blue-600 cursor-pointer"
            onClick={() => navigate("/instructor/register")}
          >
            Register
          </span>
        </p>
      </form>
    </div>
  );
};

export default InstructorLogin;
