import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const AddLecture = () => {
  const [title, setTitle] = useState("");
  const [courseId, setCourseId] = useState("");
  const [instructorId, setInstructorId] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const navigate = useNavigate();

  // Fetch courses & instructors
  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("adminToken");
        const config = { headers: { Authorization: `Bearer ${token}` } };

        const coursesRes = await axios.get(
          "http://localhost:5000/api/admin/courses",
          config
        );
        setCourses(coursesRes.data);

        const instructorsRes = await axios.get(
          "http://localhost:5000/api/admin/instructors",
          config
        );
        setInstructors(instructorsRes.data);
      } catch (err) {
        console.error(err);
        alert("Failed to fetch courses or instructors");
      }
    };
    fetchData();
  }, []);

  const handleAddLecture = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("adminToken");
      const config = { headers: { Authorization: `Bearer ${token}` } };

      await axios.post(
        "http://localhost:5000/api/admin/lectures",
        { title, courseId, instructorId, date, time },
        config
      );

      alert("Lecture added successfully!");
      navigate("/admin/lectures");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "Error adding lecture");
    }
  };

  return (
    <div className="container">
      <h2>Add Lecture</h2>
      <form onSubmit={handleAddLecture}>
        <input
          type="text"
          placeholder="Lecture Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <select
          value={courseId}
          onChange={(e) => setCourseId(e.target.value)}
          required
        >
          <option value="">Select Course</option>
          {courses.map((c) => (
            <option key={c._id} value={c._id}>
              {c.name}
            </option>
          ))}
        </select>
        <select
          value={instructorId}
          onChange={(e) => setInstructorId(e.target.value)}
          required
        >
          <option value="">Select Instructor</option>
          {instructors.map((i) => (
            <option key={i._id} value={i._id}>
              {i.name}
            </option>
          ))}
        </select>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          required
        />
        <button type="submit">Add Lecture</button>
      </form>
    </div>
  );
};

export default AddLecture;
