import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";
import { useNavigate } from "react-router-dom";

const InstructorList = () => {
  const [instructors, setInstructors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const fetchInstructors = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        navigate("/admin/login");
        return;
      }

      const response = await axios.get("http://localhost:5000/api/instructor", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setInstructors(response.data || []);
      setMessage("");
    } catch (error) {
      setMessage("Failed to load instructors.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInstructors();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this instructor?"))
      return;

    try {
      const token = localStorage.getItem("adminToken");

      await axios.delete(`http://localhost:5000/api/instructor/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setInstructors(instructors.filter((inst) => inst._id !== id));
      setMessage("Instructor deleted successfully!");
    } catch {
      setMessage("Failed to delete instructor.");
    }
  };

  return (
    <div>
      <AdminNavbar />

      <div className="container mt-5">
        <h2 className="text-2xl font-bold mb-3">Instructor List</h2>

        {message && <p className="text-red-600 mb-3">{message}</p>}
        {loading ? (
          <Loader />
        ) : (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Department</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {instructors.length === 0 ? (
                <tr>
                  <td colSpan="5" className="text-center">
                    No instructors found
                  </td>
                </tr>
              ) : (
                instructors.map((inst) => (
                  <tr key={inst._id}>
                    <td>{inst.name}</td>
                    <td>{inst.email}</td>
                    <td>{inst.phone}</td>
                    <td>{inst.department}</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDelete(inst._id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default InstructorList;
