import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Loader from "../../components/Loader";

const AdminLogin = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);

    try {
      const response = await axios.post(
        "http://localhost:5000/admin/login",
        formData
      );

      const { token } = response.data;
      localStorage.setItem("adminToken", token);
      navigate("/admin/dashboard");
    } catch (err) {
      setError(err.response?.data?.message || "Login failed. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-400 to-purple-500 px-4">
      <div className="bg-white shadow-2xl rounded-2xl p-10 max-w-md w-full transform transition-transform duration-500 hover:scale-105">
        <h2 className="text-4xl font-extrabold text-blue-700 mb-8 text-center">
          Admin Login
        </h2>

        {error && (
          <p className="text-red-600 text-center mb-4 font-medium animate-pulse">
            {error}
          </p>
        )}

        {loading ? (
          <Loader />
        ) : (
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* Email */}
            <div>
              <label className="block text-gray-700 font-medium mb-2">
                Email
              </label>
              <input
                type="email"
                name="email"
                placeholder="admin@example.com"
                value={formData.email}
                onChange={handleChange}
                className="w-full border border-gray-300 px-4 py-3 rounded-xl focus:ring-4 focus:ring-blue-300 focus:border-blue-500 outline-none transition"
                required
              />
            </div>

            {/* Password */}
            <div>
              <label className="block text-gray-700 font-medium mb-2">
                Password
              </label>
              <input
                type="password"
                name="password"
                placeholder="Enter password"
                value={formData.password}
                onChange={handleChange}
                className="w-full border border-gray-300 px-4 py-3 rounded-xl focus:ring-4 focus:ring-blue-300 focus:border-blue-500 outline-none transition"
                required
              />
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 rounded-xl text-lg font-bold shadow-lg hover:from-blue-700 hover:to-purple-700 transition duration-300"
            >
              Login
            </button>
          </form>
        )}

        <p className="text-center text-gray-500 mt-6 text-sm">
          &copy; {new Date().getFullYear()} Smart Lecture Scheduler
        </p>
      </div>
    </div>
  );
};

export default AdminLogin;
