// src/pages/admin/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalLectures: 0,
    totalInstructors: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchStats = async () => {
      setLoading(true);
      setError("");

      try {
        const token = localStorage.getItem("adminToken");
        if (!token) throw new Error("No token found. Please login.");

        const res = await axios.get("http://localhost:5000/api/admin/stats", {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Support flexible backend response
        const data = res.data;
        setStats({
          totalCourses: data.totalCourses || data.courses || 0,
          totalLectures: data.totalLectures || data.lectures || 0,
          totalInstructors: data.totalInstructors || data.instructors || 0,
        });
      } catch (err) {
        console.error(err);
        setError(
          err.response?.data?.message || err.message || "Failed to fetch stats"
        );
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <Loader />;

  return (
    <div className="min-h-screen bg-gray-100">
      <AdminNavbar />
      <div className="p-6 max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

        {error && (
          <div className="mb-4 p-3 bg-red-200 text-red-800 rounded">
            {error}
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Total Courses */}
          <div className="bg-white p-6 rounded shadow flex flex-col items-center justify-center">
            <h2 className="text-xl font-semibold mb-2">Total Courses</h2>
            <p className="text-3xl font-bold">{stats.totalCourses}</p>
          </div>

          {/* Total Lectures */}
          <div className="bg-white p-6 rounded shadow flex flex-col items-center justify-center">
            <h2 className="text-xl font-semibold mb-2">Total Lectures</h2>
            <p className="text-3xl font-bold">{stats.totalLectures}</p>
          </div>

          {/* Total Instructors */}
          <div className="bg-white p-6 rounded shadow flex flex-col items-center justify-center">
            <h2 className="text-xl font-semibold mb-2">Total Instructors</h2>
            <p className="text-3xl font-bold">{stats.totalInstructors}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
