// src/pages/instructor/InstructorDashboard.jsx
import React, { useEffect, useState } from "react";

export default function InstructorDashboard() {
  const [lectures, setLectures] = useState([]);
  const [message, setMessage] = useState("");

  const instructor = JSON.parse(localStorage.getItem("instructorData"));

  const fetchLectures = async () => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/instructor/lectures/${instructor._id}`
      );
      if (!res.ok) throw new Error("Failed to fetch lectures");
      const data = await res.json();
      setLectures(data);
    } catch (err) {
      console.error(err);
      setMessage(err.message);
    }
  };

  useEffect(() => {
    fetchLectures();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <h1 className="text-3xl font-bold mb-6">Instructor Dashboard</h1>

      {message && <p className="text-red-500 mb-4">{message}</p>}

      <div className="bg-white rounded shadow p-4">
        <h2 className="text-xl font-semibold mb-3">Assigned Lectures</h2>
        <ul className="max-h-96 overflow-auto">
          {lectures.map((l) => (
            <li key={l._id} className="border-b p-2">
              <strong>{l.title}</strong> - Course: {l.courseId?.title || "N/A"}{" "}
              - Date: {new Date(l.date).toLocaleDateString()}
            </li>
          ))}
          {lectures.length === 0 && <p>No lectures assigned yet.</p>}
        </ul>
      </div>
    </div>
  );
}
