import React, { useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AddInstructor = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    department: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  // Handle input change
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setSubmitting(true);

    try {
      const token = localStorage.getItem("adminToken");
      if (!token) throw new Error("No token found. Please login.");

      const res = await axios.post(
        "http://localhost:5000/api/admin/instructors", // ✅ correct plural URL
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage(res.data.message || "Instructor added successfully!");
      setForm({
        name: "",
        email: "",
        phone: "",
        department: "",
        password: "",
      });
    } catch (err) {
      console.error(err);
      setMessage(
        err.response?.data?.message || err.message || "Failed to add instructor"
      );
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <Loader />;

  return (
    <div className="min-h-screen bg-gray-100">
      <AdminNavbar />
      <div className="max-w-md mx-auto p-6 bg-white rounded shadow mt-6">
        <h2 className="text-2xl font-bold mb-4">Add Instructor</h2>

        {message && (
          <div className="mb-4 p-2 bg-blue-200 text-blue-800 rounded">
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={form.name}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <input
            type="text"
            name="phone"
            placeholder="Phone Number"
            value={form.phone}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <input
            type="text"
            name="department"
            placeholder="Department"
            value={form.department}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <button
            type="submit"
            disabled={submitting}
            className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
          >
            {submitting ? "Adding..." : "Add Instructor"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddInstructor;
