// src/pages/instructor/InstructorDashboard.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const InstructorDashboard = () => {
  const navigate = useNavigate();
  const [instructorName, setInstructorName] = useState("");
  const [stats, setStats] = useState({ lectures: 0, courses: 0 });
  const [loading, setLoading] = useState(true);

  // Fetch instructor info from localStorage
  useEffect(() => {
    const name = localStorage.getItem("instructorName");
    if (name) setInstructorName(name);
  }, []);

  // Fetch stats from backend
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem("instructorToken");
        const res = await axios.get(
          "http://localhost:5000/api/instructor/dashboard",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setStats(res.data);
        setLoading(false);
      } catch (error) {
        console.error(error);
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  // Logout handler
  const handleLogout = () => {
    localStorage.removeItem("instructorToken");
    localStorage.removeItem("instructorName");
    navigate("/instructor/login");
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Navbar */}
      <nav className="bg-blue-600 text-white px-6 py-4 flex justify-between items-center">
        <h1 className="font-bold text-xl">Instructor Dashboard</h1>
        <div className="flex items-center gap-4">
          <span>Welcome, {instructorName}</span>
          <button
            onClick={handleLogout}
            className="bg-white text-blue-600 px-3 py-1 rounded hover:bg-gray-200 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <div className="p-6">
        <h2 className="text-2xl font-semibold mb-4">Statistics</h2>

        {loading ? (
          <p>Loading stats...</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-white p-4 rounded shadow">
              <h3 className="text-lg font-medium">Lectures</h3>
              <p className="text-2xl font-bold">{stats.lectures}</p>
            </div>
            <div className="bg-white p-4 rounded shadow">
              <h3 className="text-lg font-medium">Courses</h3>
              <p className="text-2xl font-bold">{stats.courses}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InstructorDashboard;
