// client/src/pages/admin/AddLecture.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";
import { useNavigate } from "react-router-dom";

const AddLecture = () => {
  const [courses, setCourses] = useState([]);
  const [formData, setFormData] = useState({
    title: "",
    courseId: "",
    date: "",
    time: "",
    description: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // Fetch courses for dropdown
  const fetchCourses = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        navigate("/admin/login");
        return;
      }

      const response = await axios.get("http://localhost:5000/admin/courses", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setCourses(response.data.courses || []);
    } catch (error) {
      console.error("Error fetching courses:", error);
      if (error.response?.status === 401) {
        navigate("/admin/login");
      } else {
        setMessage("Failed to fetch courses.");
      }
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        navigate("/admin/login");
        return;
      }

      const response = await axios.post(
        "http://localhost:5000/admin/add-lecture",
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage(response.data.message || "Lecture added successfully!");
      setFormData({
        title: "",
        courseId: "",
        date: "",
        time: "",
        description: "",
      });
    } catch (error) {
      console.error("Error adding lecture:", error);
      if (error.response?.status === 401) {
        navigate("/admin/login");
      } else {
        setMessage(error.response?.data?.message || "Something went wrong!");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div
        className="container"
        style={{ maxWidth: "600px", marginTop: "50px" }}
      >
        <h2 className="text-2xl font-bold mb-4">Add Lecture</h2>

        {message && <p className="mb-4 text-center text-red-600">{message}</p>}
        {loading && <Loader />}

        <form onSubmit={handleSubmit}>
          <div className="form-group mb-3">
            <label>Title</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="form-group mb-3">
            <label>Course</label>
            <select
              name="courseId"
              value={formData.courseId}
              onChange={handleChange}
              className="form-control"
              required
            >
              <option value="">Select Course</option>
              {courses.map((course) => (
                <option key={course._id} value={course._id}>
                  {course.courseName} ({course.courseCode})
                </option>
              ))}
            </select>
          </div>

          <div className="form-group mb-3">
            <label>Date</label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="form-group mb-3">
            <label>Time</label>
            <input
              type="time"
              name="time"
              value={formData.time}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>

          <div className="form-group mb-3">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              className="form-control"
              rows="3"
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary w-full"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Lecture"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddLecture;
