// client/src/pages/admin/Dashboard.jsx
import React, { useEffect, useState } from "react";

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalAdmins: 0,
    totalInstructors: 0,
    totalCourses: 0,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        // ✅ Get token from localStorage
        const token = localStorage.getItem("adminToken");
        if (!token) {
          setError("Not authenticated. Please login as admin.");
          setLoading(false);
          return;
        }

        // ✅ Make API request with Authorization header
        const res = await fetch("http://localhost:5000/api/admin/dashboard", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // must match backend middleware
          },
        });

        // ✅ Handle errors properly
        if (!res.ok) {
          const data = await res.json();
          throw new Error(data.message || "Access denied. Not an admin.");
        }

        const data = await res.json();
        setStats(data);
        setLoading(false);
      } catch (err) {
        console.error("Dashboard fetch error:", err);
        setError(err.message);
        setLoading(false);
      }
    };

    fetchDashboard();
  }, []);

  if (loading) return <div>Loading dashboard...</div>;
  if (error)
    return (
      <div style={{ color: "red", fontWeight: "bold" }}>
        {error} <br />
        Please login again.
      </div>
    );

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <p>Total Admins: {stats.totalAdmins}</p>
        <p>Total Instructors: {stats.totalInstructors}</p>
        <p>Total Courses: {stats.totalCourses}</p>
      </div>
    </div>
  );
};

export default Dashboard;
