// src/pages/admin/Dashboard.jsx
import React, { useEffect, useState } from "react";
import AdminNavbar from "../../components/AdminNavbar";

const Dashboard = () => {
  const [stats, setStats] = useState({ admins: 0, instructors: 0, courses: 0 });
  const [loading, setLoading] = useState(true);

  // Instructor & Course states
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState({
    name: "",
    level: "",
    description: "",
    image: "",
  });
  const [lectureData, setLectureData] = useState({
    courseId: "",
    instructorId: "",
    date: "",
  });
  const [error, setError] = useState("");

  // Fetch dashboard stats, instructors, courses
  useEffect(() => {
    const fetchDashboard = async () => {
      try {
        const token = localStorage.getItem("adminToken");
        const statsRes = await fetch(
          "http://localhost:5000/api/admin/dashboard",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const statsData = await statsRes.json();
        setStats({
          admins: statsData.totalAdmins || 0,
          instructors: statsData.totalInstructors || 0,
          courses: statsData.totalCourses || 0,
        });

        const instructorsRes = await fetch(
          "http://localhost:5000/api/admin/instructors",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const instructorsData = await instructorsRes.json();
        setInstructors(instructorsData);

        const coursesRes = await fetch(
          "http://localhost:5000/api/admin/courses",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const coursesData = await coursesRes.json();
        setCourses(coursesData);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchDashboard();
  }, []);

  // Add new course
  const handleAddCourse = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("adminToken");
      const formData = new FormData();
      formData.append("name", newCourse.name);
      formData.append("level", newCourse.level);
      formData.append("description", newCourse.description);
      formData.append("image", newCourse.image);

      const res = await fetch("http://localhost:5000/api/admin/courses", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to add course");

      setCourses([...courses, data]);
      setNewCourse({ name: "", level: "", description: "", image: "" });
    } catch (err) {
      console.error(err);
      setError(err.message);
    }
  };

  // Add lecture (schedule)
  const handleAddLecture = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("adminToken");

      // Frontend conflict check
      const existingLecture = courses
        .find((c) => c._id === lectureData.courseId)
        ?.lectures?.find(
          (l) =>
            l.instructor === lectureData.instructorId &&
            l.date === lectureData.date
        );

      if (existingLecture) {
        alert("Instructor already assigned to a lecture on this date!");
        return;
      }

      const res = await fetch("http://localhost:5000/api/admin/lectures", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(lectureData),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Failed to add lecture");

      // Update courses with new lecture
      setCourses((prev) =>
        prev.map((c) =>
          c._id === lectureData.courseId
            ? { ...c, lectures: [...(c.lectures || []), data] }
            : c
        )
      );

      setLectureData({ courseId: "", instructorId: "", date: "" });
    } catch (err) {
      console.error(err);
      alert(err.message);
    }
  };

  if (loading) return <p className="p-4 text-center">Loading dashboard...</p>;

  return (
    <div>
      <AdminNavbar stats={stats} />

      <div className="p-4 grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Instructor List */}
        <div>
          <h2 className="text-xl font-semibold mb-2">Instructors</h2>
          <table className="w-full border">
            <thead className="bg-gray-200">
              <tr>
                <th className="p-2 border">Name</th>
                <th className="p-2 border">Email</th>
              </tr>
            </thead>
            <tbody>
              {instructors.map((inst) => (
                <tr key={inst._id}>
                  <td className="p-2 border">{inst.name}</td>
                  <td className="p-2 border">{inst.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Courses & Add Course */}
        <div>
          <h2 className="text-xl font-semibold mb-2">Courses</h2>
          <form
            onSubmit={handleAddCourse}
            className="mb-4 bg-gray-100 p-4 rounded"
          >
            <input
              type="text"
              placeholder="Name"
              value={newCourse.name}
              onChange={(e) =>
                setNewCourse({ ...newCourse, name: e.target.value })
              }
              className="w-full p-2 mb-2 border rounded"
              required
            />
            <input
              type="text"
              placeholder="Level"
              value={newCourse.level}
              onChange={(e) =>
                setNewCourse({ ...newCourse, level: e.target.value })
              }
              className="w-full p-2 mb-2 border rounded"
              required
            />
            <textarea
              placeholder="Description"
              value={newCourse.description}
              onChange={(e) =>
                setNewCourse({ ...newCourse, description: e.target.value })
              }
              className="w-full p-2 mb-2 border rounded"
              required
            />
            <input
              type="file"
              onChange={(e) =>
                setNewCourse({ ...newCourse, image: e.target.files[0] })
              }
              className="mb-2"
            />
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Add Course
            </button>
          </form>

          {/* List of Courses with lecture assignment */}
          {courses.map((course) => (
            <div
              key={course._id}
              className="border p-3 mb-3 rounded bg-gray-50"
            >
              <h3 className="font-semibold">{course.name}</h3>
              <p>{course.level}</p>
              <p>{course.description}</p>

              <form
                onSubmit={handleAddLecture}
                className="mt-2 flex flex-col gap-2"
              >
                <select
                  value={lectureData.courseId}
                  onChange={(e) =>
                    setLectureData({ ...lectureData, courseId: course._id })
                  }
                  className="p-2 border rounded"
                  required
                >
                  <option value="">Select Course</option>
                  <option value={course._id}>{course.name}</option>
                </select>
                <select
                  value={lectureData.instructorId}
                  onChange={(e) =>
                    setLectureData({
                      ...lectureData,
                      instructorId: e.target.value,
                    })
                  }
                  className="p-2 border rounded"
                  required
                >
                  <option value="">Select Instructor</option>
                  {instructors.map((inst) => (
                    <option key={inst._id} value={inst._id}>
                      {inst.name}
                    </option>
                  ))}
                </select>
                <input
                  type="date"
                  value={lectureData.date}
                  onChange={(e) =>
                    setLectureData({ ...lectureData, date: e.target.value })
                  }
                  className="p-2 border rounded"
                  required
                />
                <button
                  type="submit"
                  className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                >
                  Add Lecture
                </button>
              </form>

              {/* List of Lectures */}
              <div className="mt-2">
                <h4 className="font-semibold">Lectures:</h4>
                <ul className="list-disc ml-5">
                  {(course.lectures || []).map((lec) => {
                    const instructor = instructors.find(
                      (i) => i._id === lec.instructor
                    );
                    return (
                      <li key={lec._id}>
                        {lec.date} - {instructor?.name || "Unknown Instructor"}
                      </li>
                    );
                  })}
                </ul>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
