// client/src/pages/Login.jsx
import React, { useState } from "react";
import AdminLogin from "./admin/AdminLogin";
import InstructorLogin from "./instructor/InstructorLogin";

const Login = () => {
  const [userType, setUserType] = useState("admin"); // default to admin

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100">
      <h1 className="text-3xl font-bold mb-6">Login</h1>

      {/* Toggle Buttons */}
      <div className="flex space-x-4 mb-6">
        <button
          className={`px-4 py-2 rounded ${
            userType === "admin" ? "bg-blue-600 text-white" : "bg-gray-300"
          }`}
          onClick={() => setUserType("admin")}
        >
          Admin
        </button>
        <button
          className={`px-4 py-2 rounded ${
            userType === "instructor"
              ? "bg-green-600 text-white"
              : "bg-gray-300"
          }`}
          onClick={() => setUserType("instructor")}
        >
          Instructor
        </button>
      </div>

      {/* Render the appropriate login form */}
      <div className="w-full max-w-md">
        {userType === "admin" ? <AdminLogin /> : <InstructorLogin />}
      </div>
    </div>
  );
};

export default Login;
