import React, { useEffect, useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        alert("Please login first");
        return;
      }

      const res = await axios.get("http://localhost:5000/api/admin/stats", {
        headers: { Authorization: `Bearer ${token}` }, // ✅ Important!
      });

      setStats(res.data);
      setLoading(false);
    } catch (err) {
      console.error("Access denied or error:", err.response?.data?.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStats();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div>
      <h1>Dashboard Stats</h1>
      <p>Total Courses: {stats.totalCourses}</p>
      <p>Total Lectures: {stats.totalLectures}</p>
      <p>Total Instructors: {stats.totalInstructors}</p>
    </div>
  );
};

export default Dashboard;
