// client/src/pages/admin/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";
import axios from "axios";

const AdminDashboard = () => {
  const [stats, setStats] = useState({});
  const [courses, setCourses] = useState([]);
  const [lectures, setLectures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("adminToken");
  const config = { headers: { Authorization: `Bearer ${token}` } };

  useEffect(() => {
    const fetchAll = async () => {
      setLoading(true);
      try {
        const [statsRes, coursesRes, lecturesRes] = await Promise.all([
          axios.get("http://localhost:5000/api/admin/dashboard", config),
          axios.get("http://localhost:5000/api/admin/courses", config),
          axios.get("http://localhost:5000/api/admin/lectures", config),
        ]);

        setStats(statsRes.data);
        setCourses(coursesRes.data);
        setLectures(lecturesRes.data);
      } catch (err) {
        console.error("Error fetching dashboard data:", err);
        setMessage("Failed to load dashboard data");
      } finally {
        setLoading(false);
      }
    };
    fetchAll();
  }, []);

  if (loading) return <Loader />;

  return (
    <div>
      <AdminNavbar />
      <div className="container">
        <h2>Admin Dashboard</h2>
        {message && <p>{message}</p>}

        <div>
          <h3>Stats</h3>
          <ul>
            <li>Total Courses: {stats.totalCourses || 0}</li>
            <li>Total Lectures: {stats.totalLectures || 0}</li>
            <li>Total Instructors: {stats.totalInstructors || 0}</li>
          </ul>
        </div>

        <div>
          <h3>Courses</h3>
          <ul>
            {courses.map((c) => (
              <li key={c._id}>
                {c.title} - Instructor: {c.instructor?.name || "N/A"}
              </li>
            ))}
          </ul>
        </div>

        <div>
          <h3>Lectures</h3>
          <ul>
            {lectures.map((l) => (
              <li key={l._id}>
                {l.title} - {l.course?.title || "N/A"} -{" "}
                {l.instructor?.name || "N/A"} - {l.date} {l.time}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
