// src/pages/admin/AddLecture.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AddLecture = () => {
  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [lectureTitle, setLectureTitle] = useState("");
  const [selectedCourse, setSelectedCourse] = useState("");
  const [selectedInstructor, setSelectedInstructor] = useState("");
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setMessage("");

      try {
        const token = localStorage.getItem("adminToken");
        if (!token) throw new Error("No token found. Please login.");

        // Fetch instructors
        const instructorsRes = await axios.get(
          "http://localhost:5000/api/admin/instructors",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        // Handle any backend format
        const instructorsData = Array.isArray(instructorsRes.data)
          ? instructorsRes.data
          : instructorsRes.data.instructors || [];
        setInstructors(instructorsData);

        // Fetch courses
        const coursesRes = await axios.get(
          "http://localhost:5000/api/admin/courses",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        const coursesData = Array.isArray(coursesRes.data)
          ? coursesRes.data
          : coursesRes.data.courses || [];
        setCourses(coursesData);
      } catch (err) {
        console.error(err);
        setMessage(
          err.response?.data?.message || err.message || "Failed to load data"
        );
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setMessage("");

    try {
      const token = localStorage.getItem("adminToken");
      if (!token) throw new Error("No token found. Please login.");

      await axios.post(
        "http://localhost:5000/api/admin/add-lecture",
        {
          title: lectureTitle,
          courseId: selectedCourse,
          instructorId: selectedInstructor,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage("Lecture added successfully!");
      setLectureTitle("");
      setSelectedCourse("");
      setSelectedInstructor("");
    } catch (err) {
      console.error(err);
      setMessage(
        err.response?.data?.message || err.message || "Failed to add lecture"
      );
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <Loader />;

  return (
    <div className="min-h-screen bg-gray-100">
      <AdminNavbar />
      <div className="p-6 max-w-lg mx-auto">
        <h1 className="text-2xl font-bold mb-6">Add New Lecture</h1>

        {message && (
          <div className="mb-4 p-3 bg-blue-200 text-blue-800 rounded">
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow">
          {/* Lecture Title */}
          <div className="mb-4">
            <label className="block mb-1 font-semibold">Lecture Title</label>
            <input
              type="text"
              value={lectureTitle}
              onChange={(e) => setLectureTitle(e.target.value)}
              required
              className="w-full p-2 border border-gray-300 rounded"
            />
          </div>

          {/* Course Dropdown */}
          <div className="mb-4">
            <label className="block mb-1 font-semibold">Select Course</label>
            <select
              value={selectedCourse}
              onChange={(e) => setSelectedCourse(e.target.value)}
              required
              className="w-full p-2 border border-gray-300 rounded"
            >
              <option value="">-- Select Course --</option>
              {Array.isArray(courses) &&
                courses.map((course) => (
                  <option
                    key={course._id || course.id}
                    value={course._id || course.id}
                  >
                    {course.name}
                  </option>
                ))}
            </select>
          </div>

          {/* Instructor Dropdown */}
          <div className="mb-4">
            <label className="block mb-1 font-semibold">
              Select Instructor
            </label>
            <select
              value={selectedInstructor}
              onChange={(e) => setSelectedInstructor(e.target.value)}
              required
              className="w-full p-2 border border-gray-300 rounded"
            >
              <option value="">-- Select Instructor --</option>
              {Array.isArray(instructors) &&
                instructors.map((inst) => (
                  <option key={inst._id || inst.id} value={inst._id || inst.id}>
                    {inst.name}
                  </option>
                ))}
            </select>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={submitting}
            className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 transition"
          >
            {submitting ? "Adding..." : "Add Lecture"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddLecture;
