import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

// ✅ Auth Pages
import AdminLogin from "./pages/admin/AdminLogin";
// import AdminRegister from "./pages/admin/AdminRegister";
import InstructorLogin from "./pages/instructor/InstructorLogin";
// import InstructorRegister from "./pages/instructor/InstructorRegister";

// ✅ Admin Pages
import AdminDashboard from "./pages/admin/Dashboard";
import InstructorList from "./pages/admin/InstructorList";
import CourseList from "./pages/admin/CourseList";
import AddCourse from "./pages/admin/AddCourse";
import AddInstructor from "./pages/admin/AddInstructor";
import AddLecture from "./pages/admin/AddLecture";

// ✅ Instructor Pages
import InstructorDashboard from "./pages/instructor/InstructorDashboard";
import MyLectures from "./pages/instructor/MyLectures";

// ✅ Protected Routes
import ProtectedRoute from "./components/ProtectedRoute";
import ProtectedInstructorRoute from "./components/ProtectedInstructorRoute";

function App() {
  return (
    <Router>
      <Routes>
        {/* Default Redirect */}
        <Route path="/" element={<Navigate to="/login" />} />

        {/* Combined Login Redirect */}
        <Route path="/login" element={<Navigate to="/admin/login" />} />

        {/* Admin Auth */}
        <Route path="/admin/login" element={<AdminLogin />} />
        {/* <Route path="/admin/register" element={<AdminRegister />} /> */}

        {/* Instructor Auth */}
        <Route path="/instructor/login" element={<InstructorLogin />} />
        {/* <Route path="/instructor/register" element={<InstructorRegister />} /> */}

        {/* Admin Protected Routes */}
        <Route
          path="/admin/dashboard"
          element={
            <ProtectedRoute>
              <AdminDashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/instructors"
          element={
            <ProtectedRoute>
              <InstructorList />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/courses"
          element={
            <ProtectedRoute>
              <CourseList />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/add-course"
          element={
            <ProtectedRoute>
              <AddCourse />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/add-instructor"
          element={
            <ProtectedRoute>
              <AddInstructor />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/add-lecture"
          element={
            <ProtectedRoute>
              <AddLecture />
            </ProtectedRoute>
          }
        />

        {/* Instructor Protected Routes */}
        <Route
          path="/instructor/dashboard"
          element={
            <ProtectedInstructorRoute>
              <InstructorDashboard />
            </ProtectedInstructorRoute>
          }
        />
        <Route
          path="/instructor/my-lectures"
          element={
            <ProtectedInstructorRoute>
              <MyLectures />
            </ProtectedInstructorRoute>
          }
        />

        {/* Fallback */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
