import React, { useEffect, useState } from "react";

export default function Dashboard() {
  // ================= STATES =================
  const [stats, setStats] = useState({ admins: 0, instructors: 0, courses: 0 });
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lectures, setLectures] = useState([]);

  const [instrName, setInstrName] = useState("");
  const [instrEmail, setInstrEmail] = useState("");

  const [courseTitle, setCourseTitle] = useState("");
  const [courseDesc, setCourseDesc] = useState("");
  const [courseInstructorId, setCourseInstructorId] = useState("");

  const [lectureTitle, setLectureTitle] = useState("");
  const [lectureDate, setLectureDate] = useState("");
  const [lectureCourseId, setLectureCourseId] = useState("");
  const [lectureInstructorId, setLectureInstructorId] = useState("");

  // ================= FETCH DATA =================
  const fetchDashboard = async () => {
    try {
      const instructorsRes = await fetch(
        "http://localhost:5000/api/admin/instructors"
      );
      const instructorsData = await instructorsRes.json();
      setInstructors(instructorsData);

      const coursesRes = await fetch("http://localhost:5000/api/admin/courses");
      const coursesData = await coursesRes.json();
      setCourses(coursesData);

      const lecturesRes = await fetch(
        "http://localhost:5000/api/admin/lectures"
      );
      const lecturesData = await lecturesRes.json();
      setLectures(lecturesData);

      setStats({
        admins: 1, // hardcoded
        instructors: instructorsData.length,
        courses: coursesData.length,
      });
    } catch (err) {
      console.error("Fetch error:", err);
    }
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  // ================= HANDLERS =================
  const handleAddInstructor = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/instructors", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: instrName, email: instrEmail }),
      });
      const data = await res.json();
      alert(data.message);
      setInstrName("");
      setInstrEmail("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddCourse = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: courseTitle,
          description: courseDesc,
          instructorId: courseInstructorId,
        }),
      });
      const data = await res.json();
      alert(data.message);
      setCourseTitle("");
      setCourseDesc("");
      setCourseInstructorId("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddLecture = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/lectures", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: lectureTitle,
          date: lectureDate,
          courseId: lectureCourseId,
          instructorId: lectureInstructorId,
        }),
      });
      const data = await res.json();
      alert(data.message);
      setLectureTitle("");
      setLectureDate("");
      setLectureCourseId("");
      setLectureInstructorId("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    window.location.href = "/admin/login";
  };

  // ================= RENDER =================
  return (
    <div>
      {/* ================= NAVBAR ================= */}
      <div className="bg-gray-800 text-white p-4 flex flex-col md:flex-row justify-between items-center shadow-md">
        <h1 className="text-2xl font-bold mb-4 md:mb-0">Admin Dashboard</h1>
        <div className="flex flex-col md:flex-row gap-4 items-center">
          <div className="bg-gray-700 p-4 rounded shadow text-center">
            <p className="text-sm">Total Admins</p>
            <p className="text-xl font-semibold">{stats.admins}</p>
          </div>
          <div className="bg-gray-700 p-4 rounded shadow text-center">
            <p className="text-sm">Total Instructors</p>
            <p className="text-xl font-semibold">{stats.instructors}</p>
          </div>
          <div className="bg-gray-700 p-4 rounded shadow text-center">
            <p className="text-sm">Total Courses</p>
            <p className="text-xl font-semibold">{stats.courses}</p>
          </div>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 transition-colors px-4 py-2 rounded shadow"
          >
            Logout
          </button>
        </div>
      </div>

      <div className="p-4 grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
        {/* ================= ADD INSTRUCTOR ================= */}
        <div className="border p-4 rounded shadow">
          <h2 className="font-semibold text-lg mb-2">Add Instructor</h2>
          <input
            type="text"
            placeholder="Name"
            value={instrName}
            onChange={(e) => setInstrName(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <input
            type="email"
            placeholder="Email"
            value={instrEmail}
            onChange={(e) => setInstrEmail(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <button
            onClick={handleAddInstructor}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Add Instructor
          </button>

          {/* LIST INSTRUCTORS */}
          <h3 className="mt-4 font-semibold">Instructor List</h3>
          <ul className="list-disc ml-5">
            {instructors.map((i) => (
              <li key={i._id}>
                {i.name} ({i.email})
              </li>
            ))}
          </ul>
        </div>

        {/* ================= ADD COURSE ================= */}
        <div className="border p-4 rounded shadow">
          <h2 className="font-semibold text-lg mb-2">Add Course</h2>
          <input
            type="text"
            placeholder="Course Title"
            value={courseTitle}
            onChange={(e) => setCourseTitle(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <textarea
            placeholder="Description"
            value={courseDesc}
            onChange={(e) => setCourseDesc(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <select
            value={courseInstructorId}
            onChange={(e) => setCourseInstructorId(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleAddCourse}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Add Course
          </button>

          {/* LIST COURSES */}
          <h3 className="mt-4 font-semibold">Course List</h3>
          <ul className="list-disc ml-5">
            {courses.map((c) => {
              const instructor = instructors.find(
                (i) => i._id === c.instructorId
              );
              return (
                <li key={c._id}>
                  {c.title} - {instructor ? instructor.name : "No Instructor"}
                </li>
              );
            })}
          </ul>
        </div>

        {/* ================= ADD LECTURE ================= */}
        <div className="border p-4 rounded shadow col-span-1 md:col-span-2">
          <h2 className="font-semibold text-lg mb-2">Add Lecture</h2>
          <input
            type="text"
            placeholder="Lecture Title"
            value={lectureTitle}
            onChange={(e) => setLectureTitle(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <input
            type="date"
            value={lectureDate}
            onChange={(e) => setLectureDate(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <select
            value={lectureCourseId}
            onChange={(e) => setLectureCourseId(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          >
            <option value="">Select Course</option>
            {courses.map((c) => (
              <option key={c._id} value={c._id}>
                {c.title}
              </option>
            ))}
          </select>
          <select
            value={lectureInstructorId}
            onChange={(e) => setLectureInstructorId(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleAddLecture}
            className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
          >
            Add Lecture
          </button>

          {/* LIST LECTURES */}
          <h3 className="mt-4 font-semibold">Lecture List</h3>
          <ul className="list-disc ml-5">
            {lectures.map((l) => {
              const course = courses.find((c) => c._id === l.courseId);
              const instructor = instructors.find(
                (i) => i._id === l.instructorId
              );
              return (
                <li key={l._id}>
                  {l.title} ({course ? course.title : "No Course"}) -{" "}
                  {instructor ? instructor.name : "No Instructor"} on {l.date}
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </div>
  );
}
