// client/src/components/ProtectedInstructorRoute.jsx
import React from "react";
import { Navigate } from "react-router-dom";

const ProtectedInstructorRoute = ({ children }) => {
  const token = localStorage.getItem("instructorToken");

  // If token exists, allow access; otherwise redirect to instructor login
  if (!token) {
    return <Navigate to="/instructor/login" replace />;
  }

  return children;
};

export default ProtectedInstructorRoute;
