// src/pages/admin/CoursesPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const CoursesPage = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const token = localStorage.getItem("adminToken");
        const res = await axios.get("http://localhost:5000/api/admin/courses", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const coursesData = Array.isArray(res.data)
          ? res.data
          : res.data.courses || [];
        setCourses(coursesData);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  if (loading) return <Loader />;

  return (
    <div className="min-h-screen bg-gray-100">
      <AdminNavbar />
      <div className="p-6 max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold">Courses</h2>
          <Link
            to="/admin/courses/add"
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
          >
            Add Course
          </Link>
        </div>
        <table className="w-full border rounded bg-white">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">Name</th>
              <th className="p-2 border">Level</th>
              <th className="p-2 border">Description</th>
            </tr>
          </thead>
          <tbody>
            {courses.map((course) => (
              <tr key={course._id || course.id}>
                <td className="p-2 border">{course.name}</td>
                <td className="p-2 border">{course.level}</td>
                <td className="p-2 border">{course.description}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CoursesPage;
