import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  const fetchCourses = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.get("http://localhost:5000/api/courses", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCourses(res.data || []);
      setMessage("");
    } catch {
      setMessage("Failed to load courses.");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this course?")) return;

    try {
      const token = localStorage.getItem("adminToken");
      await axios.delete(`http://localhost:5000/api/courses/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCourses((prev) => prev.filter((c) => c._id !== id));
      setMessage("Course deleted successfully!");
    } catch {
      setMessage("Failed to delete course.");
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  return (
    <div>
      <AdminNavbar />
      <div className="container mx-auto mt-10 px-4">
        <h2 className="text-3xl font-bold mb-4 border-b-4 border-blue-600 inline-block pb-2">
          Course List
        </h2>
        {message && (
          <p
            className={`mb-4 p-2 text-center rounded ${
              message.includes("success")
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {message}
          </p>
        )}
        {loading ? (
          <Loader />
        ) : (
          <div className="overflow-x-auto shadow-lg rounded-lg border border-gray-200">
            <table className="min-w-full bg-white divide-y divide-gray-200">
              <thead className="bg-blue-600 text-white">
                <tr>
                  <th className="px-6 py-3 text-left text-sm font-semibold uppercase">
                    Name
                  </th>
                  <th className="px-6 py-3 text-left text-sm font-semibold uppercase">
                    Level
                  </th>
                  <th className="px-6 py-3 text-left text-sm font-semibold uppercase">
                    Description
                  </th>
                  <th className="px-6 py-3 text-center text-sm font-semibold uppercase">
                    Image
                  </th>
                  <th className="px-6 py-3 text-center text-sm font-semibold uppercase">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {courses.length === 0 ? (
                  <tr>
                    <td colSpan="5" className="text-center py-6 text-gray-500">
                      No courses found
                    </td>
                  </tr>
                ) : (
                  courses.map((course, index) => (
                    <tr
                      key={course._id}
                      className={index % 2 === 0 ? "bg-gray-50" : "bg-white"}
                    >
                      <td className="px-6 py-4 whitespace-nowrap text-gray-800 font-medium">
                        {course.name}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-gray-700">
                        {course.level}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-gray-700">
                        {course.description}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-center">
                        {course.image ? (
                          <img
                            src={`http://localhost:5000/uploads/${course.image}`}
                            alt={course.name}
                            className="w-20 h-20 object-cover rounded"
                          />
                        ) : (
                          <span className="text-gray-400">No Image</span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-center">
                        <button
                          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded shadow-md"
                          onClick={() => handleDelete(course._id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default CourseList;
