import React, { useEffect, useState } from "react";

export default function Dashboard() {
  // ================= STATES =================
  const [stats, setStats] = useState({
    admins: 1,
    instructors: 0,
    courses: 0,
    lectures: 0,
  });

  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lectures, setLectures] = useState([]);

  // Input fields
  const [instrName, setInstrName] = useState("");
  const [instrEmail, setInstrEmail] = useState("");

  const [courseTitle, setCourseTitle] = useState("");
  const [courseDesc, setCourseDesc] = useState("");
  const [courseInstructorId, setCourseInstructorId] = useState("");

  const [lectureTitle, setLectureTitle] = useState("");
  const [lectureDate, setLectureDate] = useState("");
  const [lectureCourseId, setLectureCourseId] = useState("");
  const [lectureInstructorId, setLectureInstructorId] = useState("");

  // ================= FETCH ALL =================
  const fetchDashboard = async () => {
    try {
      const instructorsRes = await fetch(
        "http://localhost:5000/api/admin/instructors"
      );
      const instructorsData = await instructorsRes.json();
      setInstructors(instructorsData);

      const coursesRes = await fetch("http://localhost:5000/api/admin/courses");
      const coursesData = await coursesRes.json();
      setCourses(coursesData);

      const lecturesRes = await fetch(
        "http://localhost:5000/api/admin/lectures"
      );
      const lecturesData = await lecturesRes.json();
      setLectures(lecturesData);

      setStats({
        admins: 1,
        instructors: instructorsData.length,
        courses: coursesData.length,
        lectures: lecturesData.length,
      });
    } catch (err) {
      console.error("Fetch error:", err);
    }
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  // ================= HANDLERS =================
  const handleAddInstructor = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/instructors", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: instrName, email: instrEmail }),
      });

      const data = await res.json();
      alert(data.message);
      setInstrName("");
      setInstrEmail("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddCourse = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: courseTitle,
          description: courseDesc,
          instructorId: courseInstructorId,
        }),
      });

      const data = await res.json();
      alert(data.message);
      setCourseTitle("");
      setCourseDesc("");
      setCourseInstructorId("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
    }
  };

  const handleAddLecture = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/lectures", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: lectureTitle,
          date: lectureDate,
          courseId: lectureCourseId,
          instructorId: lectureInstructorId,
        }),
      });

      const data = await res.json();
      alert(data.message);

      setLectureTitle("");
      setLectureDate("");
      setLectureCourseId("");
      setLectureInstructorId("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("adminToken");
    window.location.href = "/admin/login";
  };

  // ================= RENDER =================
  return (
    <div>
      {/* ================= NAVBAR / STATS ================= */}
      <div className="bg-gray-900 text-white p-6 flex flex-col md:flex-row justify-between items-center shadow-lg">
        <h1 className="text-3xl font-bold mb-4 md:mb-0">Admin Dashboard</h1>

        <div className="flex flex-wrap gap-4 items-center">
          {/* Stats Boxes */}
          {[
            { label: "Admins", value: stats.admins },
            { label: "Instructors", value: stats.instructors },
            { label: "Courses", value: stats.courses },
            { label: "Lectures", value: stats.lectures },
          ].map((box, idx) => (
            <div
              key={idx}
              className="bg-gray-700 p-4 rounded-lg shadow text-center w-32"
            >
              <p className="text-sm">{box.label}</p>
              <p className="text-xl font-bold">{box.value}</p>
            </div>
          ))}

          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded shadow"
          >
            Logout
          </button>
        </div>
      </div>

      {/* ================= FORMS ================= */}
      <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
        {/* ADD INSTRUCTOR */}
        <div className="border p-4 rounded-lg shadow">
          <h2 className="font-semibold text-lg mb-3">Add Instructor</h2>

          <input
            type="text"
            placeholder="Name"
            value={instrName}
            onChange={(e) => setInstrName(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />

          <input
            type="email"
            placeholder="Email"
            value={instrEmail}
            onChange={(e) => setInstrEmail(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />

          <button
            onClick={handleAddInstructor}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Add Instructor
          </button>

          {/* Instructor List */}
          <h3 className="mt-4 font-semibold">Instructors</h3>
          <ul className="ml-5 list-disc">
            {instructors.map((i) => (
              <li key={i._id}>
                {i.name} ({i.email})
              </li>
            ))}
          </ul>
        </div>

        {/* ADD COURSE */}
        <div className="border p-4 rounded-lg shadow">
          <h2 className="font-semibold text-lg mb-3">Add Course</h2>

          <input
            type="text"
            placeholder="Course Title"
            value={courseTitle}
            onChange={(e) => setCourseTitle(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />

          <textarea
            placeholder="Description"
            value={courseDesc}
            onChange={(e) => setCourseDesc(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />

          <select
            value={courseInstructorId}
            onChange={(e) => setCourseInstructorId(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>

          <button
            onClick={handleAddCourse}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Add Course
          </button>

          {/* Course List */}
          <h3 className="mt-4 font-semibold">Courses</h3>
          <ul className="ml-5 list-disc">
            {courses.map((c) => (
              <li key={c._id}>
                {c.title} —{" "}
                {instructors.find((i) => i._id === c.instructorId)?.name ||
                  "Unassigned"}
              </li>
            ))}
          </ul>
        </div>

        {/* ADD LECTURE (FULL WIDTH) */}
        <div className="border p-4 rounded-lg shadow md:col-span-2">
          <h2 className="font-semibold text-lg mb-3">Add Lecture</h2>

          <input
            type="text"
            placeholder="Lecture Title"
            value={lectureTitle}
            onChange={(e) => setLectureTitle(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />

          <input
            type="date"
            value={lectureDate}
            onChange={(e) => setLectureDate(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />

          <select
            value={lectureCourseId}
            onChange={(e) => setLectureCourseId(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          >
            <option value="">Select Course</option>
            {courses.map((c) => (
              <option key={c._id} value={c._id}>
                {c.title}
              </option>
            ))}
          </select>

          <select
            value={lectureInstructorId}
            onChange={(e) => setLectureInstructorId(e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>

          <button
            onClick={handleAddLecture}
            className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
          >
            Add Lecture
          </button>

          {/* Lecture List */}
          <h3 className="mt-4 font-semibold">Lectures</h3>
          <ul className="ml-5 list-disc">
            {lectures.map((l) => {
              const course = courses.find((c) => c._id === l.courseId);
              const instructor = instructors.find(
                (i) => i._id === l.instructorId
              );
              return (
                <li key={l._id}>
                  {l.title} — {course?.title || "No Course"} —{" "}
                  {instructor?.name || "No Instructor"} on {l.date}
                </li>
              );
            })}
          </ul>
        </div>
      </div>
    </div>
  );
}
