// AssignLecture.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const AssignLecture = () => {
  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [form, setForm] = useState({ instructor: "", course: "", date: "" });
  const [message, setMessage] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/admin/instructors")
      .then((res) => setInstructors(res.data));
    axios
      .get("http://localhost:5000/api/admin/courses")
      .then((res) => setCourses(res.data));
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/lecture",
        form
      );
      setMessage(res.data.message);
    } catch (err) {
      setMessage(err.response.data.error);
    }
  };

  return (
    <div>
      <h2>Assign Lecture</h2>
      <form onSubmit={handleSubmit}>
        <select name="course" onChange={handleChange} required>
          <option value="">Select Course</option>
          {courses.map((c) => (
            <option key={c._id} value={c._id}>
              {c.name}
            </option>
          ))}
        </select>
        <select name="instructor" onChange={handleChange} required>
          <option value="">Select Instructor</option>
          {instructors.map((i) => (
            <option key={i._id} value={i._id}>
              {i.name}
            </option>
          ))}
        </select>
        <input type="date" name="date" onChange={handleChange} required />
        <button type="submit">Assign</button>
      </form>
      <p>{message}</p>
    </div>
  );
};

export default AssignLecture;
