import React, { useState } from "react";
import api from "../../api/axios";
import { useNavigate } from "react-router-dom";

const InstructorRegister = () => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    email: "",
    phone: "",
    department: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    try {
      const res = await api.post("/instructor/register", form);

      localStorage.setItem("instructorToken", res.data.token);

      navigate("/instructor/dashboard");
    } catch (err) {
      alert(err.response?.data?.message || "Error registering");
    }
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form className="p-6 bg-white shadow rounded" onSubmit={handleRegister}>
        <h2 className="text-xl mb-4 font-semibold">Instructor Registration</h2>

        {Object.keys(form).map((key) =>
          key !== "password" ? (
            <input
              key={key}
              name={key}
              type="text"
              className="border p-2 w-full my-2"
              placeholder={key.charAt(0).toUpperCase() + key.slice(1)}
              value={form[key]}
              onChange={handleChange}
            />
          ) : (
            <input
              key={key}
              name={key}
              type="password"
              className="border p-2 w-full my-2"
              placeholder="Password"
              value={form[key]}
              onChange={handleChange}
            />
          )
        )}

        <button className="bg-blue-600 text-white py-2 px-4 rounded w-full">
          Register
        </button>

        <p className="mt-3 text-sm">
          Already have an account?{" "}
          <span
            className="text-blue-600 cursor-pointer"
            onClick={() => navigate("/instructor/login")}
          >
            Login
          </span>
        </p>
      </form>
    </div>
  );
};

export default InstructorRegister;
