// client/src/pages/instructor/MyLectures.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import InstructorNavbar from "../../components/InstructorNavbar";
import Loader from "../../components/Loader";

const MyLectures = () => {
  const [lectures, setLectures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("instructorToken");

  // Fetch lectures assigned to the logged-in instructor
  const fetchLectures = async () => {
    try {
      const response = await axios.get("/api/instructor/my-lectures", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setLectures(response.data.lectures || []);
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "Failed to load lectures.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLectures();
  }, []);

  return (
    <div>
      <InstructorNavbar />
      <div className="container" style={{ marginTop: "50px" }}>
        <h2>My Lectures</h2>
        {message && <p style={{ color: "red" }}>{message}</p>}
        {loading ? (
          <Loader />
        ) : (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Title</th>
                <th>Course</th>
                <th>Date</th>
                <th>Time</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {lectures.length === 0 ? (
                <tr>
                  <td colSpan="5" style={{ textAlign: "center" }}>
                    No lectures found.
                  </td>
                </tr>
              ) : (
                lectures.map((lecture) => (
                  <tr key={lecture._id}>
                    <td>{lecture.title}</td>
                    <td>{lecture.courseName}</td>
                    <td>{lecture.date}</td>
                    <td>{lecture.time}</td>
                    <td>{lecture.description}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default MyLectures;
