import React, { useEffect, useState } from "react";
import axios from "axios";
import InstructorNavbar from "../../components/InstructorNavbar";
import Loader from "../../components/Loader";

const InstructorDashboard = () => {
  const [stats, setStats] = useState({});
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchStats = async () => {
      setLoading(true);
      setMessage("");
      try {
        const token = localStorage.getItem("instructorToken");
        if (!token) {
          setMessage("Instructor not logged in");
          setLoading(false);
          return;
        }

        const response = await axios.get(
          "http://localhost:5000/api/instructor/stats",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setStats(response.data);
      } catch (error) {
        console.error(error);
        if (error.response && error.response.data) {
          setMessage(error.response.data.error || "Failed to fetch stats");
        } else {
          setMessage("Network error. Please try again.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  return (
    <div>
      <InstructorNavbar />
      <div className="p-6">
        <h1 className="text-2xl font-bold mb-4">Instructor Dashboard</h1>

        {loading && <Loader />}

        {message && !loading && (
          <p className="text-red-600 font-medium">{message}</p>
        )}

        {!loading && !message && (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="p-4 shadow rounded bg-white">
              <h2 className="text-lg font-semibold">My Courses</h2>
              <p className="text-2xl mt-2">{stats.myCourses || 0}</p>
            </div>
            <div className="p-4 shadow rounded bg-white">
              <h2 className="text-lg font-semibold">My Lectures</h2>
              <p className="text-2xl mt-2">{stats.myLectures || 0}</p>
            </div>
            <div className="p-4 shadow rounded bg-white">
              <h2 className="text-lg font-semibold">Total Students</h2>
              <p className="text-2xl mt-2">{stats.totalStudents || 0}</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default InstructorDashboard;
