// client/src/pages/admin/AddCourse.jsx
import React, { useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AddCourse = () => {
  const [formData, setFormData] = useState({
    name: "",
    level: "",
    description: "",
    image: "",
  });
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ type: "", message: "" });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAlert({ type: "", message: "" });

    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.post(
        "http://localhost:5000/api/courses",
        formData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setAlert({ type: "success", message: "✅ Course Added Successfully!" });
      setFormData({ name: "", level: "", description: "", image: "" });
    } catch (err) {
      setAlert({
        type: "error",
        message: err.response?.data?.message || "❌ Error adding course",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="container mx-auto mt-10 max-w-lg">
        <h2 className="text-3xl font-bold mb-4 border-b-4 border-green-600 inline-block pb-2">
          Add Course
        </h2>

        {alert.message && (
          <p
            className={`mb-4 p-2 text-center rounded ${
              alert.type === "success"
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {alert.message}
          </p>
        )}

        {loading && <Loader />}

        <form onSubmit={handleSubmit} className="space-y-3">
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Course Name"
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="text"
            name="level"
            value={formData.level}
            onChange={handleChange}
            placeholder="Level (Beginner, Advanced, etc.)"
            className="w-full p-2 border rounded"
            required
          />
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Description"
            className="w-full p-2 border rounded"
          />
          <input
            type="text"
            name="image"
            value={formData.image}
            onChange={handleChange}
            placeholder="Image URL"
            className="w-full p-2 border rounded"
          />
          <button
            className="w-full p-2 bg-green-600 text-white rounded"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Course"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddCourse;
