import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const [stats, setStats] = useState({
    instructors: 0,
    courses: 0,
    lectures: 0,
  });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("adminToken");
    if (!token) return navigate("/admin/login");
    fetchStats(token);
  }, []);

  const fetchStats = async (token) => {
    try {
      const res = await axios.get("http://localhost:5000/admin/stats", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setStats(res.data);
      setMessage("");
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
      if (error.response?.status === 401) navigate("/admin/login");
      else setMessage("❌ Failed to load dashboard data.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <AdminNavbar />
      <div className="w-full min-h-screen bg-gray-100 py-10 px-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">
          Admin Dashboard
        </h1>

        {loading ? (
          <Loader />
        ) : (
          <>
            {message && (
              <p className="text-red-600 font-medium mb-6 text-center">
                {message}
              </p>
            )}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white shadow-lg rounded-xl p-6 border-t-4 border-blue-600">
                <h2 className="text-xl font-semibold text-gray-700">
                  Instructors
                </h2>
                <p className="text-4xl font-bold text-blue-700 mt-3">
                  {stats.instructors}
                </p>
              </div>
              <div className="bg-white shadow-lg rounded-xl p-6 border-t-4 border-green-600">
                <h2 className="text-xl font-semibold text-gray-700">Courses</h2>
                <p className="text-4xl font-bold text-green-700 mt-3">
                  {stats.courses}
                </p>
              </div>
              <div className="bg-white shadow-lg rounded-xl p-6 border-t-4 border-purple-600">
                <h2 className="text-xl font-semibold text-gray-700">
                  Lectures
                </h2>
                <p className="text-4xl font-bold text-purple-700 mt-3">
                  {stats.lectures}
                </p>
              </div>
            </div>
          </>
        )}
      </div>
    </>
  );
};

export default Dashboard;
