// client/src/pages/admin/InstructorList.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const InstructorList = () => {
  const [instructors, setInstructors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  // Fetch instructors from backend
  const fetchInstructors = async () => {
    try {
      const response = await axios.get("/api/admin/instructors");
      setInstructors(response.data.instructors || []);
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "Failed to load instructors.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInstructors();
  }, []);

  // Delete instructor
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this instructor?"))
      return;

    try {
      await axios.delete(`/api/admin/instructor/${id}`);
      setInstructors(instructors.filter((inst) => inst._id !== id));
      setMessage("Instructor deleted successfully!");
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "Failed to delete instructor.");
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="container" style={{ marginTop: "50px" }}>
        <h2>Instructor List</h2>
        {message && <p style={{ color: "green" }}>{message}</p>}
        {loading ? (
          <Loader />
        ) : (
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Department</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {instructors.length === 0 ? (
                <tr>
                  <td colSpan="6" style={{ textAlign: "center" }}>
                    No instructors found.
                  </td>
                </tr>
              ) : (
                instructors.map((inst) => (
                  <tr key={inst._id}>
                    <td>{inst.firstName}</td>
                    <td>{inst.lastName}</td>
                    <td>{inst.email}</td>
                    <td>{inst.phone}</td>
                    <td>{inst.department}</td>
                    <td>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDelete(inst._id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default InstructorList;
