import React, { useEffect, useState } from "react";

export default function Dashboard() {
  const [stats, setStats] = useState({
    admins: 1,
    instructors: 0,
    courses: 0,
    lectures: 0,
  });

  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lectures, setLectures] = useState([]);

  const [instrName, setInstrName] = useState("");
  const [instrEmail, setInstrEmail] = useState("");

  const [courseTitle, setCourseTitle] = useState("");
  const [courseDesc, setCourseDesc] = useState("");
  const [courseInstructorId, setCourseInstructorId] = useState("");

  const [lectureTitle, setLectureTitle] = useState("");
  const [lectureDate, setLectureDate] = useState("");
  const [lectureCourseId, setLectureCourseId] = useState("");
  const [lectureInstructorId, setLectureInstructorId] = useState("");

  const [message, setMessage] = useState({ text: "", type: "" });

  const showMessage = (text, type = "success") => {
    setMessage({ text, type });
    setTimeout(() => setMessage({ text: "", type: "" }), 4000);
  };

  // FETCH INSTRUCTORS
  const fetchInstructors = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/instructors");
      if (!res.ok) throw new Error("Failed to fetch instructors");
      const data = await res.json();
      setInstructors(data);
      return data.length;
    } catch (err) {
      console.error(err);
      showMessage(err.message, "error");
      return 0;
    }
  };

  // FETCH COURSES
  const fetchCourses = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/courses");
      if (!res.ok) throw new Error("Failed to fetch courses");
      const data = await res.json();
      setCourses(data);
      return data.length;
    } catch (err) {
      console.error(err);
      showMessage(err.message, "error");
      return 0;
    }
  };

  // FETCH LECTURES
  const fetchLectures = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/lectures");
      if (!res.ok) throw new Error("Failed to fetch lectures");
      const data = await res.json();
      setLectures(data);
      return data.length;
    } catch (err) {
      console.error(err);
      showMessage(err.message, "error");
      return 0;
    }
  };

  const fetchDashboard = async () => {
    const [instrCount, courseCount, lectureCount] = await Promise.all([
      fetchInstructors(),
      fetchCourses(),
      fetchLectures(),
    ]);
    setStats({
      admins: 1,
      instructors: instrCount,
      courses: courseCount,
      lectures: lectureCount,
    });
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  const handleAddInstructor = async () => {
    if (!instrName || !instrEmail)
      return showMessage("Please fill all fields", "error");
    try {
      const res = await fetch("http://localhost:5000/api/admin/instructors", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name: instrName, email: instrEmail }),
      });
      if (!res.ok) throw new Error("Failed to add instructor");
      showMessage("Instructor added successfully");
      setInstrName("");
      setInstrEmail("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
      showMessage(err.message, "error");
    }
  };

  const handleAddCourse = async () => {
    if (!courseTitle || !courseDesc || !courseInstructorId)
      return showMessage("Please fill all fields", "error");
    try {
      const res = await fetch("http://localhost:5000/api/admin/courses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: courseTitle,
          description: courseDesc,
          instructorId: courseInstructorId,
        }),
      });
      if (!res.ok) throw new Error("Failed to add course");
      showMessage("Course added successfully");
      setCourseTitle("");
      setCourseDesc("");
      setCourseInstructorId("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
      showMessage(err.message, "error");
    }
  };

  const handleAddLecture = async () => {
    if (
      !lectureTitle ||
      !lectureDate ||
      !lectureCourseId ||
      !lectureInstructorId
    )
      return showMessage("Please fill all fields", "error");
    try {
      const res = await fetch("http://localhost:5000/api/admin/lectures", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          title: lectureTitle,
          date: lectureDate,
          courseId: lectureCourseId,
          instructorId: lectureInstructorId,
        }),
      });
      if (!res.ok) throw new Error("Failed to add lecture");
      showMessage("Lecture added successfully");
      setLectureTitle("");
      setLectureDate("");
      setLectureCourseId("");
      setLectureInstructorId("");
      fetchDashboard();
    } catch (err) {
      console.error(err);
      showMessage(err.message, "error");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* NAVBAR */}
      <div className="bg-gray-900 text-white p-6 flex flex-wrap justify-between items-center">
        <h1 className="text-3xl font-bold">Admin Dashboard</h1>
        <div className="flex gap-4">
          {[
            { label: "Admins", value: stats.admins },
            { label: "Instructors", value: stats.instructors },
            { label: "Courses", value: stats.courses },
            { label: "Lectures", value: stats.lectures },
          ].map((box, idx) => (
            <div
              key={idx}
              className="bg-gray-700 px-4 py-2 rounded text-center shadow"
            >
              <p className="text-sm">{box.label}</p>
              <p className="text-xl font-bold">{box.value}</p>
            </div>
          ))}
        </div>
      </div>

      {/* MESSAGE */}
      {message.text && (
        <div
          className={`p-3 m-4 rounded ${
            message.type === "success"
              ? "bg-green-200 text-green-800"
              : "bg-red-200 text-red-800"
          }`}
        >
          {message.text}
        </div>
      )}

      {/* CONTENT */}
      <div className="p-6 grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* ADD INSTRUCTOR */}
        <div className="p-4 bg-white rounded shadow">
          <h2 className="text-lg font-semibold mb-3">Add Instructor</h2>
          <input
            type="text"
            placeholder="Name"
            value={instrName}
            onChange={(e) => setInstrName(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          />
          <input
            type="email"
            placeholder="Email"
            value={instrEmail}
            onChange={(e) => setInstrEmail(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          />
          <button
            onClick={handleAddInstructor}
            className="bg-blue-600 text-white px-4 py-2 rounded"
          >
            Add Instructor
          </button>
        </div>

        {/* ADD COURSE */}
        <div className="p-4 bg-white rounded shadow">
          <h2 className="text-lg font-semibold mb-3">Add Course</h2>
          <input
            type="text"
            placeholder="Course Title"
            value={courseTitle}
            onChange={(e) => setCourseTitle(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          />
          <textarea
            placeholder="Description"
            value={courseDesc}
            onChange={(e) => setCourseDesc(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          />
          <select
            value={courseInstructorId}
            onChange={(e) => setCourseInstructorId(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleAddCourse}
            className="bg-green-600 text-white px-4 py-2 rounded"
          >
            Add Course
          </button>
        </div>

        {/* ADD LECTURE */}
        <div className="p-4 bg-white rounded shadow md:col-span-2">
          <h2 className="text-lg font-semibold mb-3">Add Lecture</h2>
          <input
            type="text"
            placeholder="Lecture Title"
            value={lectureTitle}
            onChange={(e) => setLectureTitle(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          />
          <input
            type="date"
            value={lectureDate}
            onChange={(e) => setLectureDate(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          />
          <select
            value={lectureCourseId}
            onChange={(e) => setLectureCourseId(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          >
            <option value="">Select Course</option>
            {courses.map((c) => (
              <option key={c._id} value={c._id}>
                {c.title}
              </option>
            ))}
          </select>
          <select
            value={lectureInstructorId}
            onChange={(e) => setLectureInstructorId(e.target.value)}
            className="w-full p-2 border rounded mb-2"
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>
          <button
            onClick={handleAddLecture}
            className="bg-purple-600 text-white px-4 py-2 rounded"
          >
            Add Lecture
          </button>
        </div>
      </div>
    </div>
  );
}
