// src/pages/admin/AddLecture.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AddLecture = ({ onAdd }) => {
  const [title, setTitle] = useState("");
  const [courseId, setCourseId] = useState("");
  const [instructorId, setInstructorId] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Get token from localStorage
  const token = localStorage.getItem("adminToken");

  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  };

  // Fetch courses and instructors
  const fetchData = async () => {
    setLoading(true);
    try {
      const [coursesRes, instructorsRes] = await Promise.all([
        axios.get("http://localhost:5000/api/admin/courses", config),
        axios.get("http://localhost:5000/api/admin/instructors", config),
      ]);

      setCourses(coursesRes.data);
      setInstructors(instructorsRes.data);
    } catch (err) {
      console.error("Error fetching data:", err);
      setMessage(
        err.response?.data?.error || "Failed to fetch courses or instructors"
      );
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (!token) {
      setMessage("You must be logged in as admin");
      return;
    }
    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Add new lecture
  const handleAddLecture = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    if (!title || !courseId || !instructorId || !date || !time) {
      setMessage("All fields are required");
      setLoading(false);
      return;
    }

    try {
      const res = await axios.post(
        "http://localhost:5000/api/admin/lectures",
        { title, courseId, instructorId, date, time },
        config
      );

      setMessage(res.data.message || "Lecture added successfully!");
      setTitle("");
      setCourseId("");
      setInstructorId("");
      setDate("");
      setTime("");

      // Refresh lecture list in parent
      if (onAdd) onAdd();
    } catch (err) {
      console.error("Error adding lecture:", err);
      setMessage(err.response?.data?.error || "Failed to add lecture");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="container">
        <h2>Add New Lecture</h2>
        {loading && <Loader />}
        {message && <p style={{ color: "red" }}>{message}</p>}

        <form onSubmit={handleAddLecture}>
          <div>
            <label>Lecture Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>

          <div>
            <label>Course:</label>
            <select
              value={courseId}
              onChange={(e) => setCourseId(e.target.value)}
              required
            >
              <option value="">Select Course</option>
              {courses.map((course) => (
                <option key={course._id} value={course._id}>
                  {course.title}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Instructor:</label>
            <select
              value={instructorId}
              onChange={(e) => setInstructorId(e.target.value)}
              required
            >
              <option value="">Select Instructor</option>
              {instructors.map((inst) => (
                <option key={inst._id} value={inst._id}>
                  {inst.name}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Date:</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>

          <div>
            <label>Time:</label>
            <input
              type="time"
              value={time}
              onChange={(e) => setTime(e.target.value)}
              required
            />
          </div>

          <button type="submit" disabled={loading}>
            Add Lecture
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddLecture;
