import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AssignLecture = () => {
  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [formData, setFormData] = useState({
    courseId: "",
    instructorId: "",
    date: "",
  });
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ type: "", message: "" });

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("adminToken");
      const coursesRes = await axios.get("http://localhost:5000/api/courses", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const instructorsRes = await axios.get(
        "http://localhost:5000/api/instructor",
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCourses(coursesRes.data);
      setInstructors(instructorsRes.data);
    };
    fetchData();
  }, []);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAlert({ type: "", message: "" });

    try {
      const token = localStorage.getItem("adminToken");
      await axios.post(
        `http://localhost:5000/api/courses/${formData.courseId}/lectures`,
        { instructorId: formData.instructorId, date: formData.date },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAlert({ type: "success", message: "Lecture assigned successfully!" });
      setFormData({ courseId: "", instructorId: "", date: "" });
    } catch (err) {
      setAlert({
        type: "error",
        message: err.response?.data?.message || "Error assigning lecture",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="container mx-auto mt-10 max-w-lg">
        <h2 className="text-3xl font-bold mb-4 border-b-4 border-blue-600 inline-block pb-2">
          Assign Lecture
        </h2>

        {alert.message && (
          <p
            className={`mb-4 p-2 text-center rounded ${
              alert.type === "success"
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {alert.message}
          </p>
        )}
        {loading && <Loader />}

        <form onSubmit={handleSubmit} className="space-y-3">
          <select
            name="courseId"
            value={formData.courseId}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select Course</option>
            {courses.map((c) => (
              <option key={c._id} value={c._id}>
                {c.name}
              </option>
            ))}
          </select>
          <select
            name="instructorId"
            value={formData.instructorId}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          >
            <option value="">Select Instructor</option>
            {instructors.map((i) => (
              <option key={i._id} value={i._id}>
                {i.name}
              </option>
            ))}
          </select>
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />
          <button
            className="w-full p-2 bg-blue-600 text-white rounded"
            disabled={loading}
          >
            {loading ? "Assigning..." : "Assign Lecture"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AssignLecture;
