// client/src/App.jsx
import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

// Admin pages
import AdminLogin from "./pages/admin/AdminLogin";
import Dashboard from "./pages/admin/Dashboard";
import AddInstructor from "./pages/admin/AddInstructor";
import AddCourse from "./pages/admin/AddCourse";
import CourseList from "./pages/admin/CourseList";
import AddLecture from "./pages/admin/AddLecture";
import InstructorList from "./pages/admin/InstructorList";

// Instructor pages
import InstructorLogin from "./pages/instructor/InstructorLogin";
import MyLectures from "./pages/instructor/MyLectures";

// Utils
import { isAdminLoggedIn, isInstructorLoggedIn } from "./utils/auth";

// Protected Route components
const AdminProtectedRoute = ({ children }) => {
  if (!isAdminLoggedIn()) return <Navigate to="/admin/login" />;
  return children;
};

const InstructorProtectedRoute = ({ children }) => {
  if (!isInstructorLoggedIn()) return <Navigate to="/instructor/login" />;
  return children;
};

const App = () => {
  return (
    <Router>
      <Routes>
        {/* Admin Routes */}
        <Route path="/admin/login" element={<AdminLogin />} />
        <Route
          path="/admin/dashboard"
          element={
            <AdminProtectedRoute>
              <Dashboard />
            </AdminProtectedRoute>
          }
        />
        <Route
          path="/admin/add-instructor"
          element={
            <AdminProtectedRoute>
              <AddInstructor />
            </AdminProtectedRoute>
          }
        />
        <Route
          path="/admin/add-course"
          element={
            <AdminProtectedRoute>
              <AddCourse />
            </AdminProtectedRoute>
          }
        />
        <Route
          path="/admin/course-list"
          element={
            <AdminProtectedRoute>
              <CourseList />
            </AdminProtectedRoute>
          }
        />
        <Route
          path="/admin/add-lecture"
          element={
            <AdminProtectedRoute>
              <AddLecture />
            </AdminProtectedRoute>
          }
        />
        <Route
          path="/admin/instructor-list"
          element={
            <AdminProtectedRoute>
              <InstructorList />
            </AdminProtectedRoute>
          }
        />

        {/* Instructor Routes */}
        <Route path="/instructor/login" element={<InstructorLogin />} />
        <Route
          path="/instructor/my-lectures"
          element={
            <InstructorProtectedRoute>
              <MyLectures />
            </InstructorProtectedRoute>
          }
        />

        {/* Default Route */}
        <Route path="*" element={<Navigate to="/admin/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
