import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import ProtectedRoute from "./components/ProtectedRoute";
import ProtectedInstructorRoute from "./components/ProtectedInstructorRoute";

// Combined Login Page
import Login from "./pages/Login"; // <-- new combined login page

// Admin pages
import Dashboard from "./pages/admin/Dashboard";
import Instructors from "./pages/admin/InstructorList";
import Courses from "./pages/admin/CourseList";
import AddCourse from "./pages/admin/AddCourse";
import AddInstructor from "./pages/admin/AddInstructor";
import AddLecture from "./pages/admin/AddLecture";

// Instructor pages
import InstructorDashboard from "./pages/instructor/InstructorDashboard";
import MyLectures from "./pages/instructor/MyLectures";

function App() {
  return (
    <Router>
      <Routes>
        {/* Default route */}
        <Route path="/" element={<Navigate to="/login" />} />

        {/* Combined Login Page */}
        <Route path="/login" element={<Login />} />

        {/* Admin Protected Routes */}
        <Route
          path="/admin/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/instructors"
          element={
            <ProtectedRoute>
              <Instructors />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/courses"
          element={
            <ProtectedRoute>
              <Courses />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/add-course"
          element={
            <ProtectedRoute>
              <AddCourse />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/add-instructor"
          element={
            <ProtectedRoute>
              <AddInstructor />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/add-lecture"
          element={
            <ProtectedRoute>
              <AddLecture />
            </ProtectedRoute>
          }
        />

        {/* Instructor Protected Routes */}
        <Route
          path="/instructor"
          element={
            <ProtectedInstructorRoute>
              <InstructorDashboard />
            </ProtectedInstructorRoute>
          }
        >
          <Route path="my-lectures" element={<MyLectures />} />
        </Route>

        {/* Fallback route */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
