import React, { useState, useEffect } from "react";
import API from "../../utils/api";
import AdminNavbar from "../../components/AdminNavbar";
import { useNavigate } from "react-router-dom";

const AddLecture = () => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState([]);
  const [instructors, setInstructors] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState("");
  const [selectedInstructor, setSelectedInstructor] = useState("");
  const [lectureDate, setLectureDate] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  // Fetch courses and instructors
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [courseRes, instructorRes] = await Promise.all([
          API.get("/admin/courses"),
          API.get("/admin/instructors"),
        ]);
        setCourses(Array.isArray(courseRes.data) ? courseRes.data : []);
        setInstructors(
          Array.isArray(instructorRes.data) ? instructorRes.data : []
        );
      } catch (err) {
        console.error(err);
        setCourses([]);
        setInstructors([]);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleAddLecture = async (e) => {
    e.preventDefault();

    if (!selectedCourse || !selectedInstructor || !lectureDate) {
      setMessage("Please fill all fields");
      return;
    }

    try {
      const res = await API.post("/admin/lecture", {
        courseId: selectedCourse,
        instructorId: selectedInstructor,
        date: lectureDate,
      });
      setMessage(res.data.message);
      setTimeout(() => navigate("/admin/dashboard"), 1000);
    } catch (err) {
      setMessage(err.response?.data?.message || "Failed to add lecture");
    }
  };

  if (loading) return <p className="p-6">Loading data...</p>;

  return (
    <div>
      <AdminNavbar />
      <div className="p-6 flex justify-center">
        <form
          className="bg-white p-6 rounded shadow-md w-96"
          onSubmit={handleAddLecture}
        >
          <h2 className="text-xl font-bold mb-4">Add Lecture</h2>
          {message && <p className="text-red-600 mb-2">{message}</p>}

          {/* Course Selection */}
          <label className="block mb-2">Select Course</label>
          <select
            value={selectedCourse}
            onChange={(e) => setSelectedCourse(e.target.value)}
            className="border p-2 w-full mb-3"
          >
            <option value="">-- Select Course --</option>
            {courses.map((course) => (
              <option key={course._id} value={course._id}>
                {course.name}
              </option>
            ))}
          </select>

          {/* Instructor Selection */}
          <label className="block mb-2">Select Instructor</label>
          <select
            value={selectedInstructor}
            onChange={(e) => setSelectedInstructor(e.target.value)}
            className="border p-2 w-full mb-3"
          >
            <option value="">-- Select Instructor --</option>
            {instructors.map((inst) => (
              <option key={inst._id} value={inst._id}>
                {inst.name} ({inst.department})
              </option>
            ))}
          </select>

          {/* Date Picker */}
          <label className="block mb-2">Lecture Date</label>
          <input
            type="date"
            value={lectureDate}
            onChange={(e) => setLectureDate(e.target.value)}
            className="border p-2 w-full mb-4"
            min={new Date().toISOString().split("T")[0]}
          />

          <button
            type="submit"
            className="bg-blue-700 text-white px-4 py-2 rounded w-full"
          >
            Add Lecture
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddLecture;
