// client/src/pages/admin/AddCourse.jsx
import React, { useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const AddCourse = () => {
  const [formData, setFormData] = useState({
    name: "",
    level: "",
    description: "",
    image: null,
  });
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ type: "", message: "" });

  const handleChange = (e) => {
    if (e.target.name === "image") {
      setFormData({ ...formData, image: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setAlert({ type: "", message: "" });

    try {
      const token = localStorage.getItem("adminToken");

      const data = new FormData();
      data.append("name", formData.name);
      data.append("level", formData.level);
      data.append("description", formData.description);
      if (formData.image) data.append("image", formData.image);

      const res = await axios.post("http://localhost:5000/api/courses", data, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      setAlert({ type: "success", message: "✅ Course Added Successfully!" });
      setFormData({ name: "", level: "", description: "", image: null });
    } catch (err) {
      setAlert({
        type: "error",
        message: err.response?.data?.message || "❌ Failed to add course",
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <AdminNavbar />

      <div className="container mx-auto mt-10 max-w-lg">
        <h2 className="text-3xl font-bold mb-4 border-b-4 border-blue-600 inline-block pb-2">
          Add Course
        </h2>

        {alert.message && (
          <p
            className={`mb-4 p-2 text-center rounded ${
              alert.type === "success"
                ? "bg-green-100 text-green-700"
                : "bg-red-100 text-red-700"
            }`}
          >
            {alert.message}
          </p>
        )}

        {loading && <Loader />}

        <form onSubmit={handleSubmit} className="space-y-3">
          <input
            type="text"
            name="name"
            placeholder="Course Name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />

          <input
            type="text"
            name="level"
            placeholder="Level (Beginner/Intermediate/Advanced)"
            value={formData.level}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            required
          />

          <textarea
            name="description"
            placeholder="Course Description"
            value={formData.description}
            onChange={handleChange}
            className="w-full p-2 border rounded"
            rows="4"
            required
          />

          <input
            type="file"
            name="image"
            accept="image/*"
            onChange={handleChange}
            className="w-full p-2 border rounded"
          />

          <button
            type="submit"
            className="w-full p-2 bg-blue-600 text-white rounded"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Course"}
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddCourse;
