// src/pages/admin/Dashboard.jsx
import React, { useEffect, useState } from "react";
import AdminNavbar from "../../components/AdminNavbar";

const Dashboard = () => {
  const [stats, setStats] = useState({ admins: 0, instructors: 0, courses: 0 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/admin/dashboard", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("adminToken")}`,
          },
        });

        if (!res.ok) {
          throw new Error("Failed to fetch stats");
        }

        const data = await res.json();
        setStats({
          admins: data.totalAdmins || 0,
          instructors: data.totalInstructors || 0,
          courses: data.totalCourses || 0,
        });
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) return <p className="p-4 text-center">Loading dashboard...</p>;

  return (
    <div>
      <AdminNavbar stats={stats} />
      <div className="p-4">
        <h2 className="text-xl font-semibold mt-4">
          Welcome to the Admin Dashboard
        </h2>
        {/* You can add other dashboard components here */}
      </div>
    </div>
  );
};

export default Dashboard;
