import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [dashboard, setDashboard] = useState({
    courses: 0,
    instructors: 0,
    lectures: 0,
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchDashboard = async () => {
      const token = localStorage.getItem("adminToken");
      if (!token) return setError("Access denied. Please log in.");

      try {
        const res = await axios.get(
          "http://localhost:5000/api/admin/dashboard",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setDashboard(res.data);
      } catch (err) {
        setError(err.response?.data?.error || "Failed to fetch dashboard");
      }
    };
    fetchDashboard();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Admin Dashboard</h1>
      {error ? (
        <p className="text-red-500">{error}</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="p-4 bg-blue-100 rounded shadow">
            <h2 className="font-bold">Courses</h2>
            <p>{dashboard.courses}</p>
          </div>
          <div className="p-4 bg-green-100 rounded shadow">
            <h2 className="font-bold">Instructors</h2>
            <p>{dashboard.instructors}</p>
          </div>
          <div className="p-4 bg-yellow-100 rounded shadow">
            <h2 className="font-bold">Lectures</h2>
            <p>{dashboard.lectures}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
