import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [dashboard, setDashboard] = useState({
    courses: 0,
    instructors: 0,
    lectures: 0,
  });
  const [error, setError] = useState("");

  const fetchDashboard = async () => {
    try {
      // Get admin token from localStorage
      const token = localStorage.getItem("adminToken");
      if (!token) {
        setError("Access denied. Please log in as admin.");
        return;
      }

      const res = await axios.get("http://localhost:5000/api/admin/dashboard", {
        headers: { Authorization: `Bearer ${token}` },
      });

      // Backend should return { courses: number, instructors: number, lectures: number }
      setDashboard(res.data);
      setError("");
    } catch (err) {
      console.error(err.response || err);
      setError(
        err.response?.data?.error ||
          err.response?.data?.message ||
          "Failed to fetch dashboard"
      );
    }
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {error ? (
        <p className="text-red-500 mb-4">{error}</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="p-4 bg-blue-100 rounded shadow">
            <h2 className="font-bold text-lg">Courses</h2>
            <p className="text-2xl">{dashboard.courses}</p>
          </div>
          <div className="p-4 bg-green-100 rounded shadow">
            <h2 className="font-bold text-lg">Instructors</h2>
            <p className="text-2xl">{dashboard.instructors}</p>
          </div>
          <div className="p-4 bg-yellow-100 rounded shadow">
            <h2 className="font-bold text-lg">Lectures</h2>
            <p className="text-2xl">{dashboard.lectures}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
