// client/src/pages/admin/AddInstructor.jsx
import React, { useState } from "react";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";
import { useNavigate } from "react-router-dom";

const AddInstructor = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const token = localStorage.getItem("adminToken");
      if (!token) return navigate("/admin/login");

      const res = await axios.post(
        "http://localhost:5000/api/instructor/add-instructor", // Correct URL
        formData,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage(res.data.message || "Instructor added successfully!");
      setFormData({ name: "", email: "", password: "" });
    } catch (error) {
      console.error("Error adding instructor:", error);
      if (error.response?.status === 401) navigate("/admin/login");
      else setMessage(error.response?.data?.message || "Something went wrong!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <AdminNavbar />
      <div className="container mx-auto mt-12 max-w-md">
        <h2 className="text-2xl font-bold mb-4">Add Instructor</h2>
        {message && <p className="mb-4 text-center text-red-600">{message}</p>}
        {loading && <Loader />}
        <form onSubmit={handleSubmit}>
          <div className="form-group mb-3">
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="form-group mb-3">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <div className="form-group mb-3">
            <label>Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className="form-control"
              required
            />
          </div>
          <button
            type="submit"
            className="btn btn-primary w-full"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Instructor"}
          </button>
        </form>
      </div>
    </>
  );
};

export default AddInstructor;
