import React, { useEffect, useState } from "react";
import API from "../../utils/api";
import InstructorNavbar from "../../components/InstructorNavbar";
import { getInstructorInfo } from "../../utils/auth";

const MyLectures = () => {
  const [lectures, setLectures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const instructor = getInstructorInfo();

  useEffect(() => {
    const fetchLectures = async () => {
      if (!instructor) {
        setError("Instructor not logged in");
        setLoading(false);
        return;
      }

      try {
        const res = await API.get(`/instructor/lectures/${instructor._id}`);
        setLectures(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        console.error(err);
        setError(err.response?.data?.message || "Failed to load lectures");
        setLectures([]);
      } finally {
        setLoading(false);
      }
    };

    fetchLectures();
  }, [instructor]);

  if (loading) return <p className="p-6">Loading lectures...</p>;
  if (error) return <p className="p-6 text-red-600">{error}</p>;

  return (
    <div>
      <InstructorNavbar />
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-4">My Lectures</h2>
        {lectures.length === 0 ? (
          <p>No lectures assigned yet.</p>
        ) : (
          <table className="border w-full">
            <thead>
              <tr className="bg-gray-200">
                <th className="border px-4 py-2">Course Name</th>
                <th className="border px-4 py-2">Lecture Date</th>
              </tr>
            </thead>
            <tbody>
              {lectures.map((lec) => (
                <tr key={lec._id}>
                  <td className="border px-4 py-2">
                    {lec.course?.name || "N/A"}
                  </td>
                  <td className="border px-4 py-2">
                    {new Date(lec.date).toLocaleDateString()}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default MyLectures;
