// client/src/pages/instructor/InstructorLogin.jsx
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const InstructorLogin = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const response = await axios.post("/api/instructor/login", {
        email,
        password,
      });
      // Save token or instructor info in localStorage
      localStorage.setItem("instructorToken", response.data.token);
      localStorage.setItem(
        "instructorData",
        JSON.stringify(response.data.instructor)
      );
      setMessage("Login successful!");
      navigate("/instructor/my-lectures"); // Redirect to instructor dashboard
    } catch (error) {
      console.error(error);
      setMessage(error.response?.data?.error || "Login failed. Try again!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="container"
      style={{ maxWidth: "400px", marginTop: "100px" }}
    >
      <h2>Instructor Login</h2>
      {message && <p style={{ color: "red" }}>{message}</p>}
      {loading && <p>Loading...</p>}
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="form-control"
            required
          />
        </div>

        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="form-control"
            required
          />
        </div>

        <button type="submit" className="btn btn-primary mt-3">
          Login
        </button>
      </form>
    </div>
  );
};

export default InstructorLogin;
