import React, { useState } from "react";
import axios from "axios";
import Loader from "../../components/Loader";
import AdminNavbar from "../../components/AdminNavbar"; // correct

const AddInstructor = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    department: "",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        setMessage("❌ Admin not logged in!");
        setLoading(false);
        return;
      }

      const res = await axios.post(
        "http://localhost:5000/api/instructor",
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage(res.data.message);
      setFormData({ name: "", email: "", phone: "", department: "" });
    } catch (error) {
      setMessage(error.response?.data?.message || "Something went wrong!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <AdminNavbar />

      <div className="container mx-auto mt-12 max-w-lg">
        <h2 className="text-2xl font-bold mb-4">Add Instructor</h2>

        {message && <p className="mb-4 text-center text-red-600">{message}</p>}
        {loading && <Loader />}

        <form onSubmit={handleSubmit} className="space-y-3">
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Name"
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Email"
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            placeholder="Phone"
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="text"
            name="department"
            value={formData.department}
            onChange={handleChange}
            placeholder="Department"
            className="w-full p-2 border rounded"
            required
          />

          <button className="w-full p-2 bg-blue-600 text-white rounded">
            {loading ? "Adding..." : "Add Instructor"}
          </button>
        </form>
      </div>
    </>
  );
};

export default AddInstructor;
