// src/pages/admin/AdminDashboard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [dashboard, setDashboard] = useState({});
  const [error, setError] = useState("");

  const fetchDashboard = async () => {
    try {
      const token = localStorage.getItem("adminToken");
      const res = await axios.get("http://localhost:5000/api/admin/dashboard", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setDashboard(res.data);
    } catch (err) {
      setError(err.response?.data?.message || "Failed to fetch dashboard");
    }
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Admin Dashboard</h1>
      {error && <p className="text-red-500">{error}</p>}
      {!error && (
        <div className="grid grid-cols-3 gap-4">
          <div className="p-4 bg-blue-100 rounded">
            <h2 className="font-bold">Courses</h2>
            <p>{dashboard.courses || 0}</p>
          </div>
          <div className="p-4 bg-green-100 rounded">
            <h2 className="font-bold">Instructors</h2>
            <p>{dashboard.instructors || 0}</p>
          </div>
          <div className="p-4 bg-yellow-100 rounded">
            <h2 className="font-bold">Lectures</h2>
            <p>{dashboard.lectures || 0}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
