import React, { useEffect, useState } from "react";

export default function Dashboard() {
  const [activePage, setActivePage] = useState("home");

  const [stats, setStats] = useState({
    admins: 1,
    instructors: 0,
    courses: 0,
    lectures: 0,
  });

  const [instructors, setInstructors] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lectures, setLectures] = useState([]);

  const [instrName, setInstrName] = useState("");
  const [instrEmail, setInstrEmail] = useState("");

  const [courseTitle, setCourseTitle] = useState("");
  const [courseDesc, setCourseDesc] = useState("");
  const [courseInstructorId, setCourseInstructorId] = useState("");

  const [lectureTitle, setLectureTitle] = useState("");
  const [lectureDate, setLectureDate] = useState("");
  const [lectureCourseId, setLectureCourseId] = useState("");
  const [lectureInstructorId, setLectureInstructorId] = useState("");

  const [message, setMessage] = useState({ text: "", type: "" });

  const showMessage = (text, type = "success") => {
    setMessage({ text, type });
    setTimeout(() => setMessage({ text: "", type: "" }), 4000);
  };

  // ------------------ FETCH FUNCTIONS ------------------
  const fetchInstructors = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/instructors");
      const data = await res.json();
      setInstructors(data);
      return data.length;
    } catch {
      return 0;
    }
  };

  const fetchCourses = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/courses");
      const data = await res.json();
      setCourses(data);
      return data.length;
    } catch {
      return 0;
    }
  };

  const fetchLectures = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/lectures");
      const data = await res.json();
      setLectures(data);
      return data.length;
    } catch {
      return 0;
    }
  };

  const fetchDashboard = async () => {
    const [instrCount, courseCount, lectureCount] = await Promise.all([
      fetchInstructors(),
      fetchCourses(),
      fetchLectures(),
    ]);

    setStats({
      admins: 1,
      instructors: instrCount,
      courses: courseCount,
      lectures: lectureCount,
    });
  };

  useEffect(() => {
    fetchDashboard();
  }, []);

  // ------------------ ADD HANDLERS ------------------
  const handleAddInstructor = async () => {
    if (!instrName || !instrEmail)
      return showMessage("Please fill all fields", "error");

    const res = await fetch("http://localhost:5000/api/admin/instructors", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name: instrName, email: instrEmail }),
    });

    if (!res.ok) return showMessage("Failed to add instructor", "error");

    showMessage("Instructor added successfully");
    setInstrName("");
    setInstrEmail("");
    fetchDashboard();
  };

  const handleAddCourse = async () => {
    if (!courseTitle || !courseDesc || !courseInstructorId)
      return showMessage("Please fill all fields", "error");

    const res = await fetch("http://localhost:5000/api/admin/courses", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: courseTitle,
        description: courseDesc,
        instructorId: courseInstructorId,
      }),
    });

    if (!res.ok) return showMessage("Failed to add course", "error");

    showMessage("Course added successfully");
    setCourseTitle("");
    setCourseDesc("");
    setCourseInstructorId("");
    fetchDashboard();
  };

  const handleAddLecture = async () => {
    if (
      !lectureTitle ||
      !lectureDate ||
      !lectureCourseId ||
      !lectureInstructorId
    )
      return showMessage("Please fill all fields", "error");

    const conflict = lectures.find(
      (l) =>
        l.instructorId?._id === lectureInstructorId &&
        new Date(l.date).toDateString() === new Date(lectureDate).toDateString()
    );

    if (conflict)
      return showMessage(
        "Instructor already has a lecture on this day!",
        "error"
      );

    const res = await fetch("http://localhost:5000/api/admin/lectures", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: lectureTitle,
        date: lectureDate,
        courseId: lectureCourseId,
        instructorId: lectureInstructorId,
      }),
    });

    if (!res.ok) return showMessage("Failed to add lecture", "error");

    showMessage("Lecture added successfully");
    setLectureTitle("");
    setLectureDate("");
    setLectureCourseId("");
    setLectureInstructorId("");
    fetchDashboard();
  };

  // ------------------ NAVBAR BUTTON STYLE ------------------
  const navBtn =
    "px-4 py-2 bg-white/20 hover:bg-white/40 text-white rounded-lg font-medium transition-all";

  // ------------------ PAGES RENDER ------------------
  const renderPage = () => {
    switch (activePage) {
      case "home":
        return (
          <div className="text-center py-10">
            <h1 className="text-4xl font-bold text-indigo-700">
              Welcome to Admin Dashboard
            </h1>
            <p className="text-gray-700 mt-4 text-lg">
              Manage instructors, courses, and lecture schedules efficiently.
            </p>
          </div>
        );
      case "addInstructor":
        return (
          <div className="max-w-xl mx-auto bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-indigo-600 mb-4">
              Add Instructor
            </h2>
            <input
              className="w-full p-3 border rounded-lg mb-3"
              placeholder="Name"
              value={instrName}
              onChange={(e) => setInstrName(e.target.value)}
            />
            <input
              className="w-full p-3 border rounded-lg mb-3"
              placeholder="Email"
              value={instrEmail}
              onChange={(e) => setInstrEmail(e.target.value)}
            />
            <button
              onClick={handleAddInstructor}
              className="bg-indigo-600 text-white px-6 py-2 rounded-lg"
            >
              Add Instructor
            </button>
          </div>
        );
      case "instructorList":
        return (
          <div className="max-w-xl mx-auto bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-indigo-600 mb-4">
              Instructors
            </h2>
            <ul className="divide-y">
              {instructors.map((i) => (
                <li key={i._id} className="py-2">
                  {i.name}  {i.email}
                </li>
              ))}
            </ul>
          </div>
        );
      case "addCourse":
        return (
          <div className="max-w-xl mx-auto bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-green-600 mb-4">
              Add Course
            </h2>
            <input
              className="w-full p-3 border rounded-lg mb-3"
              placeholder="Course Title"
              value={courseTitle}
              onChange={(e) => setCourseTitle(e.target.value)}
            />
            <textarea
              className="w-full p-3 border rounded-lg mb-3"
              placeholder="Course Description"
              value={courseDesc}
              onChange={(e) => setCourseDesc(e.target.value)}
            />
            <select
              value={courseInstructorId}
              onChange={(e) => setCourseInstructorId(e.target.value)}
              className="w-full p-3 border rounded-lg mb-3"
            >
              <option value="">Select Instructor</option>
              {instructors.map((i) => (
                <option key={i._id} value={i._id}>
                  {i.name}
                </option>
              ))}
            </select>

            <button
              onClick={handleAddCourse}
              className="bg-green-600 text-white px-6 py-2 rounded-lg"
            >
              Add Course
            </button>
          </div>
        );
      case "courseList":
        return (
          <div className="max-w-xl mx-auto bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-green-600 mb-4">
              Courses
            </h2>
            <ul className="divide-y">
              {courses.map((c) => (
                <li key={c._id} className="py-2">
                  {c.title}  Instructor: {c.instructorId?.name || "N/A"}
                </li>
              ))}
            </ul>
          </div>
        );
      case "addLecture":
        return (
          <div className="max-w-xl mx-auto bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-purple-600 mb-4">
              Add Lecture
            </h2>
            <input
              className="w-full p-3 border rounded-lg mb-3"
              placeholder="Lecture Title"
              value={lectureTitle}
              onChange={(e) => setLectureTitle(e.target.value)}
            />
            <input
              type="date"
              className="w-full p-3 border rounded-lg mb-3"
              value={lectureDate}
              onChange={(e) => setLectureDate(e.target.value)}
            />
            <select
              className="w-full p-3 border rounded-lg mb-3"
              value={lectureCourseId}
              onChange={(e) => setLectureCourseId(e.target.value)}
            >
              <option value="">Select Course</option>
              {courses.map((c) => (
                <option key={c._id} value={c._id}>
                  {c.title}
                </option>
              ))}
            </select>

            <select
              className="w-full p-3 border rounded-lg mb-3"
              value={lectureInstructorId}
              onChange={(e) => setLectureInstructorId(e.target.value)}
            >
              <option value="">Select Instructor</option>
              {instructors.map((i) => (
                <option key={i._id} value={i._id}>
                  {i.name}
                </option>
              ))}
            </select>

            <button
              onClick={handleAddLecture}
              className="bg-purple-600 text-white px-6 py-2 rounded-lg"
            >
              Add Lecture
            </button>
          </div>
        );
      case "lectureList":
        return (
          <div className="max-w-xl mx-auto bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-2xl font-semibold text-purple-600 mb-4">
              Lectures
            </h2>
            <ul className="divide-y">
              {lectures.map((l) => (
                <li key={l._id} className="py-2">
                  {l.title}  {l.courseId?.title}  {l.instructorId?.name} {" "}
                  {new Date(l.date).toLocaleDateString()}
                </li>
              ))}
            </ul>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-100 to-gray-200">
      {/* ------------------ NAVBAR ------------------ */}
      <div className="bg-gradient-to-r from-purple-700 to-indigo-700 p-5 shadow-lg flex flex-wrap gap-3 justify-center md:justify-between items-center text-white">
        <h1 className="text-3xl font-bold">Admin Dashboard</h1>

        <div className="flex gap-3 flex-wrap justify-center">
          <button className={navBtn} onClick={() => setActivePage("home")}>
            Home
          </button>
          <button
            className={navBtn}
            onClick={() => setActivePage("addInstructor")}
          >
            Add Instructor
          </button>
          <button
            className={navBtn}
            onClick={() => setActivePage("instructorList")}
          >
            Instructor List
          </button>
          <button className={navBtn} onClick={() => setActivePage("addCourse")}>
            Add Course
          </button>
          <button
            className={navBtn}
            onClick={() => setActivePage("courseList")}
          >
            Course List
          </button>
          <button
            className={navBtn}
            onClick={() => setActivePage("addLecture")}
          >
            Add Lecture
          </button>
          <button
            className={navBtn}
            onClick={() => setActivePage("lectureList")}
          >
            Lecture List
          </button>

          <button
            className="px-4 py-2 bg-red-600 hover:bg-red-700 rounded-lg"
            onClick={() => {
              localStorage.removeItem("token");
              window.location.href = "/login";
            }}
          >
            Logout
          </button>
        </div>
      </div>

      {/* ------------------ MESSAGE ------------------ */}
      {message.text && (
        <div
          className={`mx-6 mt-6 p-3 text-center rounded-lg ${
            message.type === "success" ? "bg-green-200" : "bg-red-200"
          }`}
        >
          {message.text}
        </div>
      )}

      {/* ------------------ PAGE CONTENT ------------------ */}
      <div className="p-6">{renderPage()}</div>
    </div>
  );
}
