// src/pages/admin/InstructorsPage.jsx
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import AdminNavbar from "../../components/AdminNavbar";
import Loader from "../../components/Loader";

const InstructorsPage = () => {
  const [instructors, setInstructors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchInstructors = async () => {
      try {
        const token = localStorage.getItem("adminToken");
        const res = await axios.get(
          "http://localhost:5000/api/admin/instructors",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        const instructorsData = Array.isArray(res.data)
          ? res.data
          : res.data.instructors || [];
        setInstructors(instructorsData);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchInstructors();
  }, []);

  if (loading) return <Loader />;

  return (
    <div className="min-h-screen bg-gray-100">
      <AdminNavbar />
      <div className="p-6 max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold">Instructors</h2>
          <Link
            to="/admin/instructors/add"
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
          >
            Add Instructor
          </Link>
        </div>
        <table className="w-full border rounded bg-white">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">Name</th>
              <th className="p-2 border">Email</th>
              <th className="p-2 border">Phone</th>
              <th className="p-2 border">Department</th>
            </tr>
          </thead>
          <tbody>
            {instructors.map((inst) => (
              <tr key={inst._id || inst.id}>
                <td className="p-2 border">{inst.name}</td>
                <td className="p-2 border">{inst.email}</td>
                <td className="p-2 border">{inst.phone}</td>
                <td className="p-2 border">{inst.department}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default InstructorsPage;
